INSERT INTO `t_vocabulary` VALUES ('13', 'ALU', 'Arithmetic Logical Unit', '算术逻辑单元', '算术逻辑单元(arithmetic and logic unit) 是能实现多组算术运算和逻辑运算的组合逻辑电路，简称ALU。', 'https://baike.baidu.com/item/%E7%AE%97%E6%9C%AF%E9%80%BB%E8%BE%91%E5%8D%95%E5%85%83', '1', null);
INSERT INTO `t_vocabulary` VALUES ('14', 'AM', 'Amplitude Modulation', '调幅', '使载波的振幅按照所需传送信号的变化规律而变化，但频率保持不变的调制方法。调幅在有线电或无线电通信和广播中应用甚广。\n[1] \n调幅使高频载波的振幅随信号改变的调制（AM）。其中，载波信号的振幅随着调制信号的某种特征的变换而变化。例如，0或1分别对应于无载波或有载波输出，电视的图像信号使用调幅。调频的抗干扰能力强，失真小，但服务半径小。', 'https://baike.baidu.com/item/%E8%B0%83%E5%B9%85', '3002525031', null);
INSERT INTO `t_vocabulary` VALUES ('15', 'ANN', 'Artificial Neural Network', '人工神经网络', '人工神经网络（Artificial Neural Network，即ANN ），是20世纪80 年代以来人工智能领域兴起的研究热点。它从信息处理角度对人脑神经元网络进行抽象， 建立某种简单模型，按不同的连接方式组成不同的网络。在工程与学术界也常直接简称为神经网络或类神经网络。神经网络是一种运算模型，由大量的节点（或称神经元）之间相互联接构成。每个节点代表一种特定的输出函数，称为激励函数（activation function）。每两个节点间的连接都代表一个对于通过该连接信号的加权值，称之为权重，这相当于人工神经网络的记忆。网络的输出则依网络的连接方式，权重值和激励函数的不同而不同。而网络自身通常都是对自然界某种算法或者函数的逼近，也可能是对一种逻辑策略的表达。最近十多年来，人工神经网络的研究工作不断深入，已经取得了很大的进展，其在模式识别、智能机器人、自动控制、预测估计、生物、医学、经济等领域已成功地解决了许多现代计算机难以解决的实际问题，表现出了良好的智能特性。', 'https://baike.baidu.com/item/%E4%BA%BA%E5%B7%A5%E7%A5%9E%E7%BB%8F%E7%BD%91%E7%BB%9C', '1', null);
INSERT INTO `t_vocabulary` VALUES ('16', 'ANSI', 'American National Standard Institute', '美国国家标准协会', '1918年，美国材料试验协会（ASTM）、与美国机械工程师协会（ASME）、美国矿业与冶金工程师协会（ASMME）、美国土木工程师协会（ASCE）、美国电气工程师协会（AIEE）等组织，共同成立了美国工程标准委员会（AESC）。美国政府的三个部（商务部、陆军部、海军部）也参与了该委员会的筹备工作。 1928年，AESC改组为美国标准协会（ASA）。1966年8月，又改组为美利坚合众国标准学会（USASI）。1969年10月6日改成现名：美国国家标准学会（ANSI）。', 'https://baike.baidu.com/item/%E7%BE%8E%E5%9B%BD%E5%9B%BD%E5%AE%B6%E6%A0%87%E5%87%86%E5%8D%8F%E4%BC%9A', '1', null);
INSERT INTO `t_vocabulary` VALUES ('17', 'API', 'Application Programming Interface', '应用程序设计接口', '应用程序编程接口，简称API（Application Programming Interface），就是软件系统不同组成部分衔接的约定。', 'https://baike.baidu.com/item/Application%20Programming%20Interface', '1', null);
INSERT INTO `t_vocabulary` VALUES ('18', 'APPN', 'Advanced Peer-to-Peer Network', '高级对等网络', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('19', 'ARP', 'Address Resolution Protocol ', '地址分辨/ 转换协议', '地址解析协议，即ARP（Address Resolution Protocol），是根据IP地址获取物理地址的一个TCP/IP协议。主机发送信息时将包含目标IP地址的ARP请求广播到局域网络上的所有主机，并接收返回消息，以此确定目标的物理地址；收到返回消息后将该IP地址和物理地址存入本机ARP缓存中并保留一定时间，下次请求时直接查询ARP缓存以节约资源。地址解析协议是建立在网络中各个主机互相信任的基础上的，局域网络上的主机可以自主发送ARP应答消息，其他主机收到应答报文时不会检测该报文的真实性就会将其记入本机ARP缓存；由此攻击者就可以向某一主机发送伪ARP应答报文，使其发送的信息无法到达预期的主机或到达错误的主机，这就构成了一个ARP欺骗。ARP命令可用于查询本机ARP缓存中IP地址和MAC地址的对应关系、添加或删除静态对应关系等。相关协议有RARP、代理ARP。NDP用于在IPv6中代替地址解析协议。', 'https://baike.baidu.com/item/Address%20Resolution%20Protocol%20', '1', null);
INSERT INTO `t_vocabulary` VALUES ('20', 'ARPG', 'Action Role Playing Game', '动作角色扮演游戏', 'ARPG（Action Role Playing Game），游戏术语，意思是动作角色扮演类游戏中，角色的动作（特别是攻击动作）与操作（如点击鼠标）相关的动作。', 'https://baike.baidu.com/item/%E5%8A%A8%E4%BD%9C%E8%A7%92%E8%89%B2%E6%89%AE%E6%BC%94%E6%B8%B8%E6%88%8F', '1', null);
INSERT INTO `t_vocabulary` VALUES ('21', 'ASCII', 'American Standard Code for Information Interchange', '美国信息交换标准代码', 'ASCII ((American Standard Code for Information Interchange): 美国信息交换标准代码）是基于拉丁字母的一套电脑编码系统，主要用于显示现代英语和其他西欧语言。它是最通用的信息交换标准，并等同于国际标准ISO/IEC 646。ASCII第一次以规范标准的类型发表是在1967年，最后一次更新则是在1986年，到目前为止共定义了128个字符\n[1] \n。', 'https://baike.baidu.com/item/%E7%BE%8E%E5%9B%BD%E4%BF%A1%E6%81%AF%E4%BA%A4%E6%8D%A2%E6%A0%87%E5%87%86%E4%BB%A3%E7%A0%81', '1', null);
INSERT INTO `t_vocabulary` VALUES ('22', 'ASP', 'Active Server Page', '活动服务器网页', 'Microsoft Internet Information Server 4.0中的服务器方执行环境。它在服务器上执行ActiveX脚本和ActiveX组件', 'https://baike.baidu.com/item/Active%20Server%20Page', '1', null);
INSERT INTO `t_vocabulary` VALUES ('23', 'ASP', 'Application Service Provider', '应用服务提供商', '应用服务提供商ASP（应用服务提供商）是向企业用户提供因特网（Internet）应用服务的服务机构。用户可以把自己所需要的因特网应用服务交给ASP 组织，称为“托管”，用户只需要具有网络终端和与网络连接的线路，就可以从ASP那里得到自己所需的因特网应用的服务。ASP拥有自己的网络资源、资金和网络技术专业人才，用户只要提出自己需要的业务模式和数据源。ASP就能为该企业用户开发适合其需要的应用系统。\n[1]', 'https://baike.baidu.com/item/%E5%BA%94%E7%94%A8%E6%9C%8D%E5%8A%A1%E6%8F%90%E4%BE%9B%E5%95%86', '1', null);
INSERT INTO `t_vocabulary` VALUES ('24', 'AST', 'Average Seek Time', '平均访问时间', '平均访问时间（Average access time）：指磁头找到指定数据的平均时间，通常是平均寻道时间和平均潜伏时间之和。平均访问时间最能够代表硬盘找到某一数据所用的时间，越短的平均访问时间越好，一般在11ms－18ms之间。注意：现在不少硬盘广告之中所说的平均访问时间大部分都是用平均寻道时间所代替的。', 'https://baike.baidu.com/item/%E5%B9%B3%E5%9D%87%E8%AE%BF%E9%97%AE%E6%97%B6%E9%97%B4', '1', null);
INSERT INTO `t_vocabulary` VALUES ('25', 'ATM', 'asynchronous transfer mode', '异步传输模式', '异步传输（Asynchronous Transmission）： 异步传输将比特分成小组进行传送，小组可以是8位的1个字符或更长。发送方可以在任何时刻发送这些比特组，而接收方从不知道它们会在什么时候到达。一个常见的例子是计算机键盘与主机的通信。按下一个字母键、数字键或特殊字符键，就发送一个8比特位的ASCII代码。键盘可以在任何时刻发送代码，这取决于用户的输入速度，内部的硬件必须能够在任何时刻接收一个键入的字符。ATM能够比较理想地实现各种QoS，既能够支持有连接的业务，又能支持无连接的业务。是宽带ISDN（B-ISDN）技术的典范。ATM为一种交换技术，在传送资料时，先将数位资料切割成多个固定长度的封包，之后利用光纤或DS1/ DS3传送。到达目的地后，再重新组合。ATM网络可同时将声音、影像及资料整合在一起。针对各种资讯型态，提供最佳的传输环境。', 'https://baike.baidu.com/item/%E5%BC%82%E6%AD%A5%E4%BC%A0%E8%BE%93%E6%A8%A1%E5%BC%8F', '1', null);
INSERT INTO `t_vocabulary` VALUES ('26', 'ATR', 'Automatic Target Recognition', '自动目标识别', '自动目标识别（英语：Automatic target recognition，缩写ATR）是基于传感器获取的数据识别目标或对象的算法或设备。', 'https://baike.baidu.com/item/%E8%87%AA%E5%8A%A8%E7%9B%AE%E6%A0%87%E8%AF%86%E5%88%AB', '1', null);
INSERT INTO `t_vocabulary` VALUES ('27', 'AVI', 'Audio Video Interleaved', '声音视频接口', 'Audio Video Interleaved简称AVI，即音频视频交错格式。是将语音和影像同步组合在一起的文件格式。它对视频文件采用了一种有损压缩方式，但压缩比较高，因此尽管画面质量不是太好，但其应用范围仍然非常广泛。AVI支持256色和RLE压缩。AVI信息主要应用在多媒体光盘上，用来保存电视、电影等各种影像信息。', 'https://baike.baidu.com/item/Audio%20Video%20Interleaved', '1', null);
INSERT INTO `t_vocabulary` VALUES ('28', 'BC', 'Bar code', '条形码', '条形码(barcode)是将宽度不等的多个黑条和空白，按照一定的编码规则排列，用以表达一组信息的图形标识符。常见的条形码是由反射率相差很大的黑条（简称条）和白条（简称空）排成的平行线图案。条形码可以标出物品的生产国、制造厂家、商品名称、生产日期、图书分类号、邮件起止地点、类别、日期等许多信息，因而在商品流通、图书管理、邮政管理、银行系统等许多领域都得到广泛的应用\n[1] \n。', 'https://baike.baidu.com/item/%E6%9D%A1%E5%BD%A2%E7%A0%81', '4158389231', null);
