INSERT INTO `t_vocabulary` VALUES ('48', 'BRI', 'Bits Per Second', '基本速率接口', '基本速率接口(BRI)是访问ISDN的一种方法，它由两个B信道和一个D信道组成，对应于CCITT建议号I．430，因此有时称它为2B+D接口。为了通过ISDN网络建立两个端点用户之间的连接，这种接口使用电路交换透明的导管，实现这种接口的设备有PBX、个别终端、电视会议装置、PC和工作站等。\n[1]', 'https://baike.baidu.com/item/%E5%9F%BA%E6%9C%AC%E9%80%9F%E7%8E%87%E6%8E%A5%E5%8F%A3', '1', null);
