INSERT INTO `t_vocabulary` VALUES ('57', 'CU', 'Control unit', '操纵单元', '控制单元（Control Unit）负责程序的流程管理。正如工厂的物流分配部门，控制单元是整个CPU的指挥控制中心，由指令寄存器IR(Instruction Register)、指令译码器ID(Instruction Decoder)和操作控制器OC(Operation Controller)三个部件组成，对协调整个电脑有序工作极为重要。控制单元可以作为CPU的一部分，也可以安装于CPU外部。', 'https://baike.baidu.com/item/Control%20unit', '1', null);
INSERT INTO `t_vocabulary` VALUES ('58', 'COWS', 'Cordless or wireless mouse', '无线鼠标', '无线鼠标是指无线缆直接连接到主机的鼠标，采用无线技术与计算机通信，从而省却电线的束缚。通常采用无线通信方式，包括蓝牙、Wi-Fi (IEEE 802.11)、Infrared (IrDA)、ZigBee (IEEE 802.15.4)等多个无线技术标准。', 'https://baike.baidu.com/item/%E6%97%A0%E7%BA%BF%E9%BC%A0%E6%A0%87', '4021149675', null);
INSERT INTO `t_vocabulary` VALUES ('59', 'CM', 'Cable modems', '有线调制解调器', '《有线电视调制解调器》是一款设计用于发送并接收在宽带CATV网络上的两个数据通道的网络信号的通信设备。', 'https://baike.baidu.com/item/%E6%9C%89%E7%BA%BF%E8%B0%83%E5%88%B6%E8%A7%A3%E8%B0%83%E5%99%A8', '1', null);
