INSERT INTO `t_vocabulary` VALUES ('65', 'CS', 'Communication channel ', '信道', '一是指词语，表示知道的意思，二是指通信的通道，是信号传输的媒介。', 'https://baike.baidu.com/item/%E4%BF%A1%E9%81%93', '3585758963', null);
INSERT INTO `t_vocabulary` VALUES ('66', 'CS', 'Communication systems', '信息系统', '信息系统（Information system），是由计算机硬件、网络和通信设备、计算机软件、信息资源、信息用户和规章制度组成的以处理信息流为目的的人机一体化系统。主要有五个基本功能，即对信息的输入、存储、处理、输出和控制。信息系统经历了简单的数据处理信息系统、孤立的业务管理信息系统、集成的智能信息系统三个发展阶段。\n[1]', 'https://baike.baidu.com/item/%E4%BF%A1%E6%81%AF%E7%B3%BB%E7%BB%9F', '4286559727', null);
INSERT INTO `t_vocabulary` VALUES ('67', 'CD', 'Compact disc', '光盘', '“光盘行动”倡导厉行节约，反对铺张浪费，带动大家珍惜粮食、吃光盘子中的食物，得到从中央到民众的支持，成为2013年十大新闻热词、网络热度词汇，最知名公益品牌之一。光盘行动由一个热心公益人发起，其新浪微博光盘行动官微于2013年1月14日、腾讯微博光盘行动于1月13日起开始宣传，1月16日于北京正式启动，全国媒体和民众、餐厅和院校等纷纷响应，全国两会、国际媒体和联合国均参与支持。 光盘行动的宗旨：餐厅不多点、食堂不多打、厨房不多做。光盘行动的目的是养成生活中珍惜粮食、厉行节约反对浪费的习惯，而不要只是一场行动。不只是在餐厅吃饭打包，而是按需点菜，在食堂按需打饭，在家按需做饭。正在发起的“光盘行动”，试图提醒与告诫人们：饥饿感距离我们并不遥远，而即便时至今日，尊重粮食仍是需要被奉行的古老美德之一。', 'https://baike.baidu.com/item/%E5%85%89%E7%9B%98', '518209503', null);
INSERT INTO `t_vocabulary` VALUES ('68', 'CST', 'Computer Science and Technology', '计算机科学与技术', 'Computer Science and Technology是术语，是指计算机科学与技术。', 'https://baike.baidu.com/item/Computer%20Science%20and%20Technology', '1', null);
