INSERT INTO `t_vocabulary` VALUES ('78', 'CPU', 'central processing unit', '中央处理器', '中央处理器（CPU，central processing unit）作为计算机系统的运算和控制核心，是信息处理、程序运行的最终执行单元。CPU 自产生以来，在逻辑结构、运行效率以及功能外延上取得了巨大发展。\n[1]', 'https://baike.baidu.com/item/%E4%B8%AD%E5%A4%AE%E5%A4%84%E7%90%86%E5%99%A8', '1', null);
INSERT INTO `t_vocabulary` VALUES ('79', 'CISC', 'complex instruction set computer', '复杂指令集计算机', '计算机处理器包含有实现各种功能的指令或微指令，指令集越丰富，为微处理器编写程序就越容易，但是丰富的微指令集会影响其性能。复杂指令集计算机（CISC）体系结构的设计策略是使用大量的指令，包括复杂指令。与其他设计相比，在CISC中进行程序设计要比在其他设计中容易，因为每一项简单或复杂的任务都有一条对应的指令。程序设计者不需要写一大堆指令去完成一项复杂的任务。 但指令集的复杂性使得CPU和控制单元的电路非常复杂。[1]', 'https://baike.baidu.com/item/%E5%A4%8D%E6%9D%82%E6%8C%87%E4%BB%A4%E9%9B%86%E8%AE%A1%E7%AE%97%E6%9C%BA', '1', null);
INSERT INTO `t_vocabulary` VALUES ('80', 'CRT', 'cathode-ray tube', '阴极射线管', '阴极射线管，是一种能减少阴极加热器耗电的阴极射线管。其中，旁热式阴极结构体，具备热电子发射物质层的金属基底；在一端的部位上设有保持基底金属，在内部还设有收纳加热器游离电子的管状套筒；加热器的主要部分筒径较大，加热器腿部一侧的筒径较小，而且也是支承套筒的异形支承体。', 'https://baike.baidu.com/item/%E9%98%B4%E6%9E%81%E5%B0%84%E7%BA%BF%E7%AE%A1', '1', null);
INSERT INTO `t_vocabulary` VALUES ('81', 'CAD', 'Computer Aided Design', '计算机辅助设计', '利用计算机及其图形设备帮助设计人员进行设计工作。简称CAD。 在工程和产品设计中，计算机可以帮助设计人员担负计算、信息存储和制图等项工作。在设计中通常要用计算机对不同方案进行大量的计算、分析和比较，以决定最优方案；各种设计信息，不论是数字的、文字的或图形的，都能存放在计算机的内存或外存里，并能快速地检索；设计人员通常用草图开始设计，将草图变为工作图的繁重工作可以交给计算机完成；利用计算机可以进行与图形的编辑、放大、缩小、平移和旋转等有关的图形数据加工工作。', 'https://baike.baidu.com/item/%E8%AE%A1%E7%AE%97%E6%9C%BA%E8%BE%85%E5%8A%A9%E8%AE%BE%E8%AE%A1', '1', null);
INSERT INTO `t_vocabulary` VALUES ('82', 'CAE', 'Computer-Aided Engineering', '计算机辅助工程', '计算机辅助工程（Computer Aided Engineering，CAE）技术的提出就是要把工程（生产）的各个环节有机地组织起来，其关键就是将有关的信息集成，使其产生并存在于工程（产品）的整个生命周期。因此，CAE系统是一个包括了相关人员、技术、经营管理及信息流和物流的有机集成且优化运行的复杂的系统。', 'https://baike.baidu.com/item/%E8%AE%A1%E7%AE%97%E6%9C%BA%E8%BE%85%E5%8A%A9%E5%B7%A5%E7%A8%8B', '1', null);
INSERT INTO `t_vocabulary` VALUES ('83', 'CAI', 'Computer Aided Instruction', '计算机辅助教学', '计算机辅助教学(Computer Aided Instruction ，简称CAI)是在计算机辅助下进行的各种教学活动，以对话方式与学生讨论教学内容、安排教学进程、进行教学训练的方法与技术。', 'https://baike.baidu.com/item/%E8%AE%A1%E7%AE%97%E6%9C%BA%E8%BE%85%E5%8A%A9%E6%95%99%E5%AD%A6', '1', null);
INSERT INTO `t_vocabulary` VALUES ('84', 'CAM', 'Computer Aided Manufacturing', '计算机辅助管理', '计算机辅助管理CAM (Computer Adied management），是指利用电子计算机高速、准确、大量地处理数据的能力帮助管理人员处理各项业务工作。它是现代管理（见管理工程）的重要手段。', 'https://baike.baidu.com/item/%E8%AE%A1%E7%AE%97%E6%9C%BA%E8%BE%85%E5%8A%A9%E7%AE%A1%E7%90%86', '1', null);
INSERT INTO `t_vocabulary` VALUES ('85', 'CASE', 'Computer Assisted Software Engineering', '计算机辅助软件工程', '计算机辅助软件工程(Computer Aided Software Engineering，CASE)原来是指用来支持MIS开发的、由各种计算机辅助软件和工具组成的一个大型综合性软件开发环境，随着各种工具及软件技术的发展、完善和不断集成，逐步由单纯的辅助开发工具环境转化为一种相对独立的方法。\n[1]', 'https://baike.baidu.com/item/%E8%AE%A1%E7%AE%97%E6%9C%BA%E8%BE%85%E5%8A%A9%E8%BD%AF%E4%BB%B6%E5%B7%A5%E7%A8%8B', '1', null);
INSERT INTO `t_vocabulary` VALUES ('86', 'CAT', 'Computer Aided Test', '计算机辅助测试', '计算机辅助测试（Computer-aided test）是指利用计算机协助进行测试的一种方法。计算机辅助测试可以用在不同的领域。在教学领域，可以使用计算机对学生的学习效果进行测试和学习能力估量，一般分为脱机测试和联机测试两种方法。在软件测试领域，可以使用计算机来进行软件的测试，提高测试效率。', 'https://baike.baidu.com/item/%E8%AE%A1%E7%AE%97%E6%9C%BA%E8%BE%85%E5%8A%A9%E6%B5%8B%E8%AF%95', '1', null);
INSERT INTO `t_vocabulary` VALUES ('87', 'CATV', 'Community Antenna Television', '有线电视', '有线电视cable television (CATV）也叫电缆电视，是由无线电视发展而来，最初出现于1950年的美国宾夕法尼亚州。有线电视仍保留了无线电视的广播制式和信号调制方式，并未改变电视系统的基本性能。有线电视把录制好的节目通过线缆(电缆或光缆)送给用户，再用电视机重放出来，而不向空中辐射电磁波，所以又叫闭路电视。有线电视是一种由观众而不是资助者支付费用的付费电视。2017年12月1日，《公共服务领域英文译写规范》正式实施，规定有线电视标准英文名为Cable TV。\n[1]', 'https://baike.baidu.com/item/%E6%9C%89%E7%BA%BF%E7%94%B5%E8%A7%86', '1543483283', null);
INSERT INTO `t_vocabulary` VALUES ('88', 'CB', 'control bus', ' 控制总线', '控制总线（ControlBus）简称CB。控制总线主要用来传送控制信号和时序信号。控制信号中，有的是微处理器送往存储器和输入输出设备接口电路的，比如：读/写信号、片选信号、中断响应信号等；也有是其它部件反馈给CPU的，比如：中断申请信号、复位信号、总线请求信号、设备就绪信号等。因此，控制总线的传送方向由具体控制信号而定，一般是双向的，控制总线的位数要根据系统的实际控制需要而定。实际上控制总线的具体情况主要取决于CPU。\n[1] \n控制总线是连接在一起并完成和实现它们之间的通讯与数据传送的，因此总线的概念是理解PC和主板的组成结构、工作原理及部件之间相互关系统的基础。这些控制信息包括CPU对内存和输入输出接口的读写信号，输入输出接口对CPU提出的中断请求或DMA请求信号，CPU对这些输入输出接口回答与响应信号，输入输出接口的各种工作状态信号以及其他各种功能控制信号。控制总线来往于CPU、内存和输入输出设备之间。', 'https://baike.baidu.com/item/%20%E6%8E%A7%E5%88%B6%E6%80%BB%E7%BA%BF', '1', null);
INSERT INTO `t_vocabulary` VALUES ('89', 'CCP', 'Communication Control Procrssor', '通信控制处理机', '对各主计算机之间、主计算机与远程数据终端之间，以及各远程数据终端之间的数据传输和交换进行控制的装置。不同功能的通信控制处理机能把多台主计算机、通信线路和很多用户终端连接成计算机通信网，使这些用户能同时使用网中的计算机，共享资源。', 'https://baike.baidu.com/item/%E9%80%9A%E4%BF%A1%E6%8E%A7%E5%88%B6%E5%A4%84%E7%90%86%E6%9C%BA', '1', null);
INSERT INTO `t_vocabulary` VALUES ('90', 'CD-R', 'Compact Disc-Recordable', '可录光盘，只写一次的光盘', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('91', 'CDFS', 'Compact Disk File System', '密集磁盘文件系统', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('92', 'CDMA', 'Code Division Multiple Access', '码分多路访问', '码分多址是指利用码序列相关性实现的多址通信\n[1] \n。码分多址(CDMA)的基本思想是靠不同的地址码来区分的地址。每个配有不同的地址码，用户所发射的载波(为同一载波)既受基带数字信号调制，又受地址码调制，接收时，只有确知其配给地址码的接收机，才能解调出相应的基带信号，而其他接收机因地址码不同，无法解调出信号。划分是根据码型结构不同来实现和识别的。一般选择伪随机码(PN码)作地址码。由于PN码的码元宽度远小于PCM信号码元宽度(通常为整数倍)，这就使得加了伪随机码的信号频谱远大于原基带信号的频谱，因此，码分多址也称为扩频多址\n[2] \n。', 'https://baike.baidu.com/item/Code%20Division%20Multiple%20Access', '1', null);
INSERT INTO `t_vocabulary` VALUES ('93', 'CGA', 'Color Graphics Adapter', '彩色显示器', '彩显是一款增强型来电显示业务，旗下包含彩名和彩像两个业务，客户办理彩显业务后，可以在拨打或接听电话的过程中通过来电显示向对方推送更多的附加信息（资讯或对方名片信息等），并且可以将个人感兴趣的图片、动画、文字在通话的过程中通过来电屏幕和他人进行分享。', 'https://baike.baidu.com/item/%E5%BD%A9%E8%89%B2%E6%98%BE%E7%A4%BA%E5%99%A8', '1', null);
INSERT INTO `t_vocabulary` VALUES ('94', 'CGI', 'common gateway interface', '公共网关接口', 'CGI全称是“公共网关接口”(Common Gateway Interface)，HTTP服务器与你的或其它机器上的程序进行“交谈”的一种工具，其程序须运行在网络服务器上。', 'https://baike.baidu.com/item/%E5%85%AC%E5%85%B1%E7%BD%91%E5%85%B3%E6%8E%A5%E5%8F%A3', '1', null);
INSERT INTO `t_vocabulary` VALUES ('95', 'CI', 'Computational Intelligence', '计算智能', '计算智能是以生物进化的观点认识和模拟智能。按照这一观点，智能是在生物的遗传、变异、生长以及外部环境的自然选择中产生的。在用进废退、优胜劣汰的过程中，适应度高的（头脑）结构被保存下来，智能水平也随之提高。因此说计算智能就是基于结构演化的智能。', 'https://baike.baidu.com/item/%E8%AE%A1%E7%AE%97%E6%99%BA%E8%83%BD', '4210425831', null);
INSERT INTO `t_vocabulary` VALUES ('96', 'COM', 'Component object model', '组件对象模型', '组件对象模型(Component Object Model,COM) 是基于Windows 平台的一套组件对象接口标准，由一组构造规范和组件对象库组成。一般的对象是由数据成员和作用在其上的方法组成，而组件对象和一般对象虽有相似性,但又有较大不同。组件对象不使用方法而用接口来描述自身。接口被定义为“在对象上实现的一组语义上相关的功能”，其实质是一组函数指针表，每个指针必须初始化指向某个具体的函数体。一个组件对象实现的接口数量没有限制。\n[1]', 'https://baike.baidu.com/item/%E7%BB%84%E4%BB%B6%E5%AF%B9%E8%B1%A1%E6%A8%A1%E5%9E%8B', '1', null);
INSERT INTO `t_vocabulary` VALUES ('97', 'CORBA', 'Common Object Request Broker Architecture', '公共对象请求代理结构', '公用对象请求代管者体系结构(CORBA) CORBA是对象管理组(OMG)在其对象管理体系结构(OMA)中所定义的基本对象消息传递技术规范。', 'https://baike.baidu.com/item/Common%20Object%20Request%20Broker%20Architecture', '1', null);
INSERT INTO `t_vocabulary` VALUES ('98', 'CRC', 'cyclical redundancy check', '循环冗余校验码', '循环冗余校验码（CRC），简称循环码，是一种常用的、具有检错、纠错能力的校验码，在早期的通信中运用广泛。循环冗余校验码常用于外存储器和计算机同步通信的数据校验。奇偶校验码和海明校验码都是采用奇偶检测为手段检错和纠错的(奇偶校验码不具有纠错能力)，而循环冗余校验则是通过某种数学运算来建立数据位和校验位的约定关系的。\n[1-2]', 'https://baike.baidu.com/item/%E5%BE%AA%E7%8E%AF%E5%86%97%E4%BD%99%E6%A0%A1%E9%AA%8C%E7%A0%81', '1', null);
INSERT INTO `t_vocabulary` VALUES ('99', 'CRM', 'Client Relation Management ', '客户关系管理', '客户关系管理是指企业为提高核心竞争力，利用相应的信息技术以及互联网技术协调企业与顾客间在销售、营销和服务上的交互，从而提升其管理方式，向客户提供创新式的个性化的客户交互和服务的过程。其最终目标是吸引新客户、保留老客户以及将已有客户转为忠实客户，增加市场。', 'https://baike.baidu.com/item/%E5%AE%A2%E6%88%B7%E5%85%B3%E7%B3%BB%E7%AE%A1%E7%90%86', '1', null);
INSERT INTO `t_vocabulary` VALUES ('100', 'CRT', 'Cathode-Ray Tube', '阴极射线管,显示器', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('101', 'CSMA', 'Carrier Sense Multi -Access', '载波侦听多路访问', '载波侦听多路访问（英语：Carrier Sense Multiple Access，缩写：CSMA）是一种介质访问控制（MAC）的协议。载波侦听（Carrier Sense） 指任何连接到介质的设备在欲发送帧前，必须对介质进行侦听，当确认其空闲时，才可以发送。 多路访问（Multiple Access） 指多个设备可以同时访问介质，一个设备发送的帧也可以被多个设备接收。', 'https://baike.baidu.com/item/%E8%BD%BD%E6%B3%A2%E4%BE%A6%E5%90%AC%E5%A4%9A%E8%B7%AF%E8%AE%BF%E9%97%AE', '1', null);
INSERT INTO `t_vocabulary` VALUES ('102', 'CSU', 'Channel Service Unit', '信道服务单元', '信道服务单元（CSU）', 'https://baike.baidu.com/item/%E4%BF%A1%E9%81%93%E6%9C%8D%E5%8A%A1%E5%8D%95%E5%85%83', '1', null);
INSERT INTO `t_vocabulary` VALUES ('103', 'CU', 'Control Unit', '控制单元', '控制单元（Control Unit）负责程序的流程管理。正如工厂的物流分配部门，控制单元是整个CPU的指挥控制中心，由指令寄存器IR(Instruction Register)、指令译码器ID(Instruction Decoder)和操作控制器OC(Operation Controller)三个部件组成，对协调整个电脑有序工作极为重要。控制单元可以作为CPU的一部分，也可以安装于CPU外部。', 'https://baike.baidu.com/item/%E6%8E%A7%E5%88%B6%E5%8D%95%E5%85%83', '2583314037', null);
INSERT INTO `t_vocabulary` VALUES ('104', 'DB', 'Database', '数据库', '数据库是“按照数据结构来组织、存储和管理数据的仓库”。是一个长期存储在计算机内的、有组织的、可共享的、统一管理的大量数据的集合。数据库是以一定方式储存在一起、能与多个用户共享、具有尽可能小的冗余度、与应用程序彼此独立的数据集合，可视为电子化的文件柜——存储电子文件的处所，用户可以对文件中的数据进行新增、查询、更新、删除等操作。\n[1]', 'https://baike.baidu.com/item/%E6%95%B0%E6%8D%AE%E5%BA%93', '3956455765', null);
INSERT INTO `t_vocabulary` VALUES ('105', 'DBF', 'database files', '数据库文件', '数据库文件 顾名思义就是指电脑上储存数据的文件。', 'https://baike.baidu.com/item/%E6%95%B0%E6%8D%AE%E5%BA%93%E6%96%87%E4%BB%B6', '1', null);
INSERT INTO `t_vocabulary` VALUES ('106', 'DBM', 'Database manager', '数据库管理', '数据库管理属数据库维护的范围。广义而言，是数据库设计以后的一切数据库管理活动。包括数据库模型创建、数据加载、数据库系统日常维护活动等。狭义而言，是数据库系统运行期间采取对数据库的活动。如数据服务、性能监督、数据库重组、数据库重构、数据库完整性控制和安全性控制、数据库恢复等各个方面。数据库管理职责由数据库管理员(DBA)承担。数据库管理的目的是为数据库用户提供一个可用性好、安全可靠、性能优秀的数据库环境。\n[1]', 'https://baike.baidu.com/item/%E6%95%B0%E6%8D%AE%E5%BA%93%E7%AE%A1%E7%90%86', '1', null);
INSERT INTO `t_vocabulary` VALUES ('107', 'DBMS', 'Database manager system', '数据库管理系统', '数据库管理系统(Database Management System)是一种操纵和管理数据库的大型软件，用于建立、使用和维护数据库，简称DBMS。它对数据库进行统一的管理和控制，以保证数据库的安全性和完整性。用户通过DBMS访问数据库中的数据，数据库管理员也通过DBMS进行数据库的维护工作。它可以支持多个应用程序和用户用不同的方法在同时或不同时刻去建立，修改和询问数据库。大部分DBMS提供数据定义语言DDL（Data Definition Language）和数据操作语言DML（Data Manipulation Language），供用户定义数据库的模式结构与权限约束，实现对数据的追加、删除等操作。\n[1]', 'https://baike.baidu.com/item/%E6%95%B0%E6%8D%AE%E5%BA%93%E7%AE%A1%E7%90%86%E7%B3%BB%E7%BB%9F', '1', null);
INSERT INTO `t_vocabulary` VALUES ('108', 'DP', 'Data projector', '数码放映机', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('109', 'DF', 'Destination file', '目标文件', '包含在 ASCII及扩展 ASCII 字符中编写的数据或程序指令的文件。计算机文件基本上分为二种：二进制文件和 ASCII（也称纯文本文件），图形文件及文字处理程序等计算机程序都属于二进制文件。这些文件含有特殊的格式及计算机代码。ASCII 则是可以用任何文字处理程序阅读的简单文本文件。', 'https://baike.baidu.com/item/%E7%9B%AE%E6%A0%87%E6%96%87%E4%BB%B6', '1', null);
INSERT INTO `t_vocabulary` VALUES ('110', 'DS', 'data security', '数据安全', '国际标准化组织（ISO）对计算机系统安全的定义是：为数据处理系统建立和采用的技术和管理的安全保护，保护计算机硬件、软件和数据不因偶然和恶意的原因遭到破坏、更改和泄露。由此计算机网络的安全可以理解为：通过采用各种技术和管理措施，使网络系统正常运行，从而确保网络数据的可用性、完整性和保密性。所以，建立网络安全保护措施的目的是确保经过网络传输和交换的数据不会发生增加、修改、丢失和泄露等。', 'https://baike.baidu.com/item/%E6%95%B0%E6%8D%AE%E5%AE%89%E5%85%A8', '2482958207', null);
INSERT INTO `t_vocabulary` VALUES ('111', 'DTS', 'Data transmission specifications', '数据传输说明', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('112', 'DBA', 'database administrator', '数据库管理员', '数据库管理员（Database Administrator，简称DBA），是从事管理和维护数据库管理系统(DBMS)的相关工作人员的统称，属于运维工程师的一个分支，主要负责业务数据库从设计、测试到部署交付的全生命周期管理。DBA的核心目标是保证数据库管理系统的稳定性、安全性、完整性和高性能。在国外，也有公司把DBA称作数据库工程师(Database Engineer)，两者的工作内容基本相同，都是保证数据库服务7*24小时的稳定高效运转，但是需要区分一下DBA和数据库开发工程师(Database Developer)：1)  数据库开发工程师的主要职责是设计和开发数据库管理系统和数据库应用软件系统，侧重于软件研发；2)  DBA的主要职责是运维和管理数据库管理系统，侧重于运维管理。', 'https://baike.baidu.com/item/%E6%95%B0%E6%8D%AE%E5%BA%93%E7%AE%A1%E7%90%86%E5%91%98', '1', null);
INSERT INTO `t_vocabulary` VALUES ('113', 'DVD', 'Digital Versatile Disc', '数字多功能光盘', '数字多功能光盘（Digital versatile disc），这种光盘与以往的光盘介质不同，它的盘片厚度薄，可以由两层0.6mm的盘片粘合在一起，容量可达9.4GB，甚至更高，因而图像清晰度高，高保真效果也更好。采用波长更短的红色激光，更有效的调制方法和更强的纠错方法，可支持双面结构。DVD将成为流行的新一代光盘，成为CD-ROM的替代品。', 'https://baike.baidu.com/item/%E6%95%B0%E5%AD%97%E5%A4%9A%E5%8A%9F%E8%83%BD%E5%85%89%E7%9B%98', '1', null);
INSERT INTO `t_vocabulary` VALUES ('114', 'DNS', 'Domain name system', '域名服务器', 'DNS（Domain Name Server，域名服务器）是进行域名(domain name)和与之相对应的IP地址 (IP address)转换的服务器。DNS中保存了一张域名(domain name)和与之相对应的IP地址 (IP address)的表，以解析消息的域名。 域名是Internet上某一台计算机或计算机组的名称，用于在数据传输时标识计算机的电子方位（有时也指地理位置）。域名是由一串用点分隔的名字组成的，通常包含组织名，而且始终包括两到三个字母的后缀，以指明组织的类型或该域所在的国家或地区。', 'https://baike.baidu.com/item/%E5%9F%9F%E5%90%8D%E6%9C%8D%E5%8A%A1%E5%99%A8', '1', null);
INSERT INTO `t_vocabulary` VALUES ('115', 'DAC', 'Digital to Analogue Converter', '数模转换器', '数模转换器，又称D/A转换器，简称DAC，它是把数字量转变成模拟的器件。D/A转换器基本上由4个部分组成，即权电阻网络、运算放大器、基准电源和模拟开关。模数转换器中一般都要用到数模转换器，模数转换器即A/D转换器，简称ADC，它是把连续的模拟信号转变为离散的数字信号的器件。', 'https://baike.baidu.com/item/%E6%95%B0%E6%A8%A1%E8%BD%AC%E6%8D%A2%E5%99%A8', '1', null);
INSERT INTO `t_vocabulary` VALUES ('116', 'DAO', 'Data Access Object', ' 数据访问对象', '数据访问对象(Data Access Objects,DAO)DAO是第一个面向对象的接口，它是露丁MerofJet数据岸引擎(《由Miroft Acce 所使用》，并允许VauatBasce安者通过ODBC像直接连接到其他数据库一样，直接连接到Aces 表。DAO最适用于单系统应用程序或小范围本地分布使用DAO是一种应用程序编程接口CAPD,DA 对象封闭了ASS的Je 函数。通过jet函数，它还可以访问其他的结构化查询语言(SQL) 数据库。12EE 开发人员使用数据访问对象(DAO 设计模式把底层的数据访问逻辑和高层的高务逻辑分开，实现DAO模式能够更加专注于编写数据访问代码。\n[1]', 'https://baike.baidu.com/item/%20%E6%95%B0%E6%8D%AE%E8%AE%BF%E9%97%AE%E5%AF%B9%E8%B1%A1', '1', null);
INSERT INTO `t_vocabulary` VALUES ('117', 'DAP', 'Directory Acces s Protocol', '目录访问协议', '目录系统协议（DSP：Directory System Protocol）主要控制两个或多个目录系统代理间、目录用户代理和目录系统代理间的交互操作。具体实现过程是：终端用户在不知道某特定信息的具体位置的情况下，就可以访问目录中的信息。', 'https://baike.baidu.com/item/%E7%9B%AE%E5%BD%95%E8%AE%BF%E9%97%AE%E5%8D%8F%E8%AE%AE', '1', null);
INSERT INTO `t_vocabulary` VALUES ('118', 'DCE', 'data communication equipment', '数据通信设备', '数据通信设备（data communication equipment），是指数据通信系统中交换设备、传输设备和终端设备的总称。', 'https://baike.baidu.com/item/%E6%95%B0%E6%8D%AE%E9%80%9A%E4%BF%A1%E8%AE%BE%E5%A4%87', '1', null);
INSERT INTO `t_vocabulary` VALUES ('119', 'DCOM', 'Distributed COM ', '分布式组件对象模型', 'DCOM（分布式组件对象模型,分布式组件对象模式）是一系列微软的概念和程序接口，利用这个接口，客户端程序对象能够请求来自网络中另一台计算机上的服务器程序对象。DCOM基于组件对象模型（COM），COM提供了一套允许同一台计算机上的客户端和服务器之间进行通信的接口（运行在Windows95或者其后的版本上）。', 'https://baike.baidu.com/item/%E5%88%86%E5%B8%83%E5%BC%8F%E7%BB%84%E4%BB%B6%E5%AF%B9%E8%B1%A1%E6%A8%A1%E5%9E%8B', '1', null);
INSERT INTO `t_vocabulary` VALUES ('120', 'DDB', 'Distributed DataBase', '分布式数据库', '分布式数据库系统通常使用较小的计算机系统，每台计算机可单独放在一个地方，每台计算机中都可能有DBMS的一份完整拷贝副本，或者部分拷贝副本，并具有自己局部的数据库，位于不同地点的许多计算机通过网络互相连接，共同组成一个完整的、全局的逻辑上集中、物理上分布的大型数据库。', 'https://baike.baidu.com/item/%E5%88%86%E5%B8%83%E5%BC%8F%E6%95%B0%E6%8D%AE%E5%BA%93', '1', null);
INSERT INTO `t_vocabulary` VALUES ('121', 'DDE', ' Dynamic Data Exchange', '动态数据交换', 'DDE是一种动态数据交换机制（Dynamic Data Exchange，DDE）。使用DDE通讯需要两个Windows应用程序，其中一个作为服务器处理信息，另外一个作为客户机从服务器获得信息。客户机应用程序向当前所激活的服务器应用程序发送一条消息请求信息，服务器应用程序根据该信息作出应答，从而实现两个程序之间的数据交换。', 'https://baike.baidu.com/item/%E5%8A%A8%E6%80%81%E6%95%B0%E6%8D%AE%E4%BA%A4%E6%8D%A2', '1', null);
INSERT INTO `t_vocabulary` VALUES ('122', 'DDI', ' Device Driver Interface', '设备驱动程序接口', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('123', 'DDK', 'Driver Development Kit', '驱动程序开发工具包', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('124', 'DDN', 'Data Digital Network', '数据数字网', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('125', 'DEC', 'Digital Equipment Corporation', '数字设备公司', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('126', 'DFS', 'Depth First Search', '深度优先搜索', '深度优先搜索是一种在开发爬虫早期使用较多的方法。它的目的是要达到被搜索结构的叶结点(即那些不包含任何超链的HTML文件) 。在一个HTML文件中，当一个超链被选择后，被链接的HTML文件将执行深度优先搜索，即在搜索其余的超链结果之前必须先完整地搜索单独的一条链。深度优先搜索沿着HTML文件上的超链走到不能再深入为止，然后返回到某一个HTML文件，再继续选择该HTML文件中的其他超链。当不再有其他超链可选择时，说明搜索已经结束。\n[1]', 'https://baike.baidu.com/item/%E6%B7%B1%E5%BA%A6%E4%BC%98%E5%85%88%E6%90%9C%E7%B4%A2', '1', null);
INSERT INTO `t_vocabulary` VALUES ('127', 'DFS', 'Distributed File System', '分布式文件系统', '分布式文件系统（Distributed File System）是指文件系统管理的物理存储资源不一定直接连接在本地节点上，而是通过计算机网络与节点(可简单的理解为一台计算机)相连。分布式文件系统的设计基于客户机/服务器模式。一个典型的网络可能包括多个供多用户访问的服务器。另外，对等特性允许一些系统扮演客户机和服务器的双重角色。例如，用户可以“发表”一个允许其他客户机访问的目录，一旦被访问，这个目录对客户机来说就像使用本地驱动器一样，下面是三个基本的分布式文件系统。\n[1]', 'https://baike.baidu.com/item/%E5%88%86%E5%B8%83%E5%BC%8F%E6%96%87%E4%BB%B6%E7%B3%BB%E7%BB%9F', '1', null);
INSERT INTO `t_vocabulary` VALUES ('128', 'DHCP', 'Dynamic Host Configuration Protocol', '动态主机配置协议', '动态主机配置协议 DHCP（Dynamic Host Configuration Protocol，动态主机配置协议） 是 RFC 1541（已被 RFC 2131 取代）定义的标准协议，该协议允许服务器向客户端动态分配 IP 地址和配置信息。DHCP协议支持C/S（客户端/服务器）结构，主要分为两部分：1、DHCP客户端：通常为网络中的PC、打印机等终端设备，使用从DHCP服务器分配下来的IP信息，包括IP地址、DNS等。2、DHCP服务器：所有的IP网络设定信息都由DHCP服务器集中管理，并处理客户端的DHCP请求。DHCP采用UDP作为传输协议，客户端发送消息到DHCP服务器的的67号端口，服务器返回消息给客户端的68号端口。', 'https://baike.baidu.com/item/%E5%8A%A8%E6%80%81%E4%B8%BB%E6%9C%BA%E9%85%8D%E7%BD%AE%E5%8D%8F%E8%AE%AE', '1', null);
INSERT INTO `t_vocabulary` VALUES ('129', 'DIB', 'Dual Independent Bus', '双独立总线', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('130', 'DIC', 'Digital Image Control', '数字图像控制', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('131', 'DLC', 'Data Link Control', '数据链路控制', '数据链路控制（data link control），是指一条数据链路上的各数据站之间为了正确地交换数据而进行的控制，简称DLC。两个设备之间真正交换数字数据时，需要用到某种形式的数据链路控制。这是由于存在传输差错的可能性；而且数据接收方有时需要调整数据到达速率，否则可能因来不及处理收到的数据而使接收缓存溢出。', 'https://baike.baidu.com/item/%E6%95%B0%E6%8D%AE%E9%93%BE%E8%B7%AF%E6%8E%A7%E5%88%B6', '1', null);
INSERT INTO `t_vocabulary` VALUES ('132', 'DLL', 'Dynamic Link Library', '动态链接库', '码环跟踪环路简称码环，其主要功能是保持复制C/A码与接收C/A码之间相位一致，从而得到对接收信号的码相位及其伪距测量值。码环的实现形式通常表现为延迟锁定环路DLL（Delay Locked Loop）。', 'https://baike.baidu.com/item/%E5%8A%A8%E6%80%81%E9%93%BE%E6%8E%A5%E5%BA%93', '1', null);
INSERT INTO `t_vocabulary` VALUES ('133', 'DLT', 'Data Link Terminal', '数据链路终端', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('134', 'DMA', 'Direct Memory Access ', '直接内存访问', '直接内存访问（DMA，Direct Memory Access）是一些计算机总线架构提供的功能，它能使数据从附加设备（如磁盘驱动器）直接发送到计算机主板的内存上。', 'https://baike.baidu.com/item/%E7%9B%B4%E6%8E%A5%E5%86%85%E5%AD%98%E8%AE%BF%E9%97%AE', '1', null);
INSERT INTO `t_vocabulary` VALUES ('135', 'DMSP', 'Distributed Mail System Protocol', '分布式电子邮件系统协议', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('136', 'DNS', 'Domain Name System', '域名系统', '域名系统（英文：Domain Name System，缩写：DNS）是互联网的一项服务。它作为将域名和IP地址相互映射的一个分布式数据库，能够使人更方便地访问互联网。DNS使用TCP和UDP端口53。当前，对于每一级域名长度的限制是63个字符，域名总长度则不能超过253个字符。', 'https://baike.baidu.com/item/%E5%9F%9F%E5%90%8D%E7%B3%BB%E7%BB%9F', '4276092927', null);
INSERT INTO `t_vocabulary` VALUES ('137', 'DOM', 'Document Object Mode', '文档对象模型', '文档对象模型（Document Object Model，简称DOM），是W3C组织推荐的处理可扩展置标语言的标准编程接口。它是一种与平台和语言无关的应用程序接口(API),它可以动态地访问程序和脚本,更新其内容、结构和www文档的风格(目前,HTML和XML文档是通过说明部分定义的)。文档可以进一步被处理，处理的结果可以加入到当前的页面。DOM是一种基于树的API文档，它要求在处理过程中整个文档都表示在存储器中。另外一种简单的API是基于事件的SAX，它可以用于处理很大的XML文档，由于大，所以不适合全部放在存储器中处理。', 'https://baike.baidu.com/item/%E6%96%87%E6%A1%A3%E5%AF%B9%E8%B1%A1%E6%A8%A1%E5%9E%8B', '1', null);
INSERT INTO `t_vocabulary` VALUES ('138', 'DOS', 'Disk Operation System', '磁盘操作系统', 'dos，是磁盘操作系统的缩写，是个人计算机上的一类操作系统。从1981年直到1995年的15年间，磁盘操作系统在IBM PC 兼容机市场中占有举足轻重的地位。而且，若是把部分以DOS为基础的Microsoft Windows版本，如Windows 95、Windows 98和Windows Me等都算进去的话，那么其商业寿命至少可以算到2000年。微软的所有后续版本中，磁盘操作系统仍然被保留着。从1981年MS-DOS1.0直到1995年MS-DOS7.1的15年间，DOS作为微软公司在个人计算机上使用的一个操作系统载体，推出了多个版本。DOS在IBM PC 兼容机市场中占有举足轻重的地位。可以直接操纵管理硬盘的文件，以DOS的形式运行。是一个更久远的操作系统CP/M的翻版。DOS家族包括MS-DOS、PC-DOS、DR-DOS、Free-DOS、PTS-DOS、ROM-DOS、JM-OS等，其中以MS-DOS最为著名，最自由开放的则是Free-DOS。虽然这些系统常被简称为\"DOS\"，但没有任何一个系统单纯以\"DOS\"命名（只有一个毫无关连的1960年代IBM大型主机操作系统以此命名）。此外，有几个和DOS无关、在非x86的微计算机系统上运行的磁盘操作系统在名称中也有\"DOS\"字眼，而且在专门讨论该机器的场合中也会简称为\"DOS\"（例如：AmigaDOS、AMSDOS、ANDOS、Apple DOS、Atari DOS、Commodore DOS、CSI-DOS、ProDOS、TRS-DOS等），但这些系统和DOS可执行文件以及MS-DOS API并不兼容。微软图形界面操作系统 Windows NT 问世以来，DOS就是一个后台程序的形式出现的。可以通过点击运行-CMD进入运行。', 'https://baike.baidu.com/item/%E7%A3%81%E7%9B%98%E6%93%8D%E4%BD%9C%E7%B3%BB%E7%BB%9F', '1', null);
INSERT INTO `t_vocabulary` VALUES ('139', 'DRAM', 'Dynamic Random Access Memory', '动态随机存取存储器', '动态随机存取存储器（Dynamic Random Access Memory，DRAM）是一种半导体存储器，主要的作用原理是利用电容内存储电荷的多寡来代表一个二进制比特（bit）是1还是0。由于在现实中晶体管会有漏电电流的现象，导致电容上所存储的电荷数量并不足以正确的判别数据，而导致数据毁损。因此对于DRAM来说，周期性地充电是一个无可避免的要件。由于这种需要定时刷新的特性，因此被称为“动态”存储器。相对来说，静态存储器（SRAM）只要存入数据后，纵使不刷新也不会丢失记忆。', 'https://baike.baidu.com/item/%E5%8A%A8%E6%80%81%E9%9A%8F%E6%9C%BA%E5%AD%98%E5%8F%96%E5%AD%98%E5%82%A8%E5%99%A8', '1', null);
INSERT INTO `t_vocabulary` VALUES ('140', 'DSD', 'Direct Stream Digital', '直接数字信号流', 'DSD是Direct Stream Digital的缩写，表示直接比特流数字编码，是SACD(Super Audio CD)的编码模式。它是Sony与Philips在1996年宣布共同发展的高解析数字音响规格。', 'https://baike.baidu.com/item/Direct%20Stream%20Digital', '1', null);
INSERT INTO `t_vocabulary` VALUES ('141', 'DSL', 'Digital Subscriber Line', '数字用户线路', 'DSL（Digital Subscriber Line）的中文名是数字用户线路，是以电话线为传输介质的传输技术组合。DSL包括ADSL(Asymmetric Digital Subscriber Line，非对称数字用户线)、RADSL、HDSL和VDSL等等\n[1] \n。DSL技术在传递公用电话网络的用户环路上支持对称和非对称传输模式，解决了经常发生在网络服务供应商和最终用户间的“最后一公里”的传输瓶颈问题。由于DSL 接入方案无需对电话线路进行改造，可以充分利用可以已经被大量铺设的电话用户环路，大大降低额外的开销。因此，利用铜缆电话线提供更高速率的因特网接入，更受用户的欢迎，在一些国家和地区得到大量应用。', 'https://baike.baidu.com/item/%E6%95%B0%E5%AD%97%E7%94%A8%E6%88%B7%E7%BA%BF%E8%B7%AF', '1', null);
INSERT INTO `t_vocabulary` VALUES ('142', 'DSP', 'Digital Signal Processing', '数字信号处理', '《数字信号处理》这门课介绍的是：将事物的运动变化转变为一串数字，并用计算的方法从中提取有用的信息，以满足我们实际应用的需求。', 'https://baike.baidu.com/item/%E6%95%B0%E5%AD%97%E4%BF%A1%E5%8F%B7%E5%A4%84%E7%90%86', '1', null);
INSERT INTO `t_vocabulary` VALUES ('143', 'DTE', 'Data Terminal Equipment', '数据终端设备', '数据终端设备（data terminal equipment）,是数据通信系统中的端设备或端系统，简称DTE。它可以是一个数据源（数据的发生者），也可以是一个数据宿（数据的接受者），或者两者都是。', 'https://baike.baidu.com/item/%E6%95%B0%E6%8D%AE%E7%BB%88%E7%AB%AF%E8%AE%BE%E5%A4%87', '1', null);
INSERT INTO `t_vocabulary` VALUES ('144', 'DVI', ' Digital Video Interactive', '数字视频交互', '数字视频交互是指具有实时压缩、解压缩和显示图像功能的一种全数字化多媒体技术。DVI是全数字声、文、图处理系统，能支持多种多媒体信息来源和信息格式。此外，DVI是一个开放系统，可被移植到各种平台上，因此赢得了广大用户，世界上有几百家公司为其开发软件，将其应用推广到各行各业中去。', 'https://baike.baidu.com/item/%E6%95%B0%E5%AD%97%E8%A7%86%E9%A2%91%E4%BA%A4%E4%BA%92', '1', null);
INSERT INTO `t_vocabulary` VALUES ('145', 'EC', 'Embedded Controller', '嵌入式控制器', '在以太控制网络中嵌入式控制器是一种很好的、基本的接入设备。嵌入式控制器是用于执行指定独立控制功能并具有复杂方式处理数据能力的控制系统。它是由嵌入式微电子技术芯片（包括微处理器芯片、定时器、序列发生器或控制器等一系列微电子器件）来控制的电子设备或装置，能够完成监视、控制等各种自动化处理任务。', 'https://baike.baidu.com/item/%E5%B5%8C%E5%85%A5%E5%BC%8F%E6%8E%A7%E5%88%B6%E5%99%A8', '1', null);
INSERT INTO `t_vocabulary` VALUES ('146', 'EDIF', ' Electronic Data Interchange Format', '电子数据交换格式', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('147', 'EEPROM', 'Erasable and Electrically Programmable ROM', '电擦除可编程只读存储器', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('148', 'EGA', 'Enhanced Graphics Adapter', '彩色显示器,分辨率为 640×350 ,可以显示 16 种颜色', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('149', 'EGP', 'External Gateway Protocol', '外部网关协议', '外部网关协议（Exterior Gateway Protocol）是AS之间使用的路由协议，最初于1982年由BBN技术公司的EricC.Rosen及DavidL.Mills提出。其最早在RFC827中描述，并于1984年在RFC904中被正式规范。EGP是一种简单的（网络）可达性协议，其与现代的距离-矢量协议和路径-矢量协议不同，它仅限适用于树状拓扑的网络。由于EGP存在很多的局限性，IETF边界网关协议工作组制定了标准的边界网关协议（BGP），当前被广泛使用。', 'https://baike.baidu.com/item/%E5%A4%96%E9%83%A8%E7%BD%91%E5%85%B3%E5%8D%8F%E8%AE%AE', '1', null);
INSERT INTO `t_vocabulary` VALUES ('150', 'EISA', 'Extended Industry Standard Architecture', '增强工业标准结构', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('151', 'EMS', 'Expanded Memory Specification', ' 扩充存储器规范', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('152', 'EPH', 'Electronic payment Handler', '电子支付处理系统', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('153', 'ETM', 'ExTended Memory', '扩展存储器', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('154', 'FAT', 'File Allocation Table', '文件分配表', '文件分配表FAT（File Allocation Table），指的是用来记录文件所在位置的表格。它对于硬盘的使用是非常重要的，假若丢失文件分配表，那么硬盘上的数据就无法定位而不能使用了。', 'https://baike.baidu.com/item/%E6%96%87%E4%BB%B6%E5%88%86%E9%85%8D%E8%A1%A8', '1', null);
INSERT INTO `t_vocabulary` VALUES ('155', 'FCB', 'File Control Block', '文件控制块', '为了能对一个文件进行正确的存取，操作系统必须为文件设置用于描述和控制文件的数据结构，称之为“文件控制块（FCB）”。', 'https://baike.baidu.com/item/%E6%96%87%E4%BB%B6%E6%8E%A7%E5%88%B6%E5%9D%97', '1', null);
INSERT INTO `t_vocabulary` VALUES ('156', 'FCFS', 'First Come First Service', '先到先服务', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('157', 'FCS ', 'Frame Check Sequence', '帧校验序列', '帧检验序列即 frame check sequence,让接收帧的网卡或接口判断是否发生了错误。', 'https://baike.baidu.com/item/%E5%B8%A7%E6%A0%A1%E9%AA%8C%E5%BA%8F%E5%88%97', '1', null);
INSERT INTO `t_vocabulary` VALUES ('158', 'FDD', 'Floppy Disk Device', '软盘驱动器', '软盘驱动器（floppy disk driver）就是平常所说的软驱”，它是读取3.5英寸或5.25英寸软盘的设备。软盘驱动器由于其存储容量小，渐渐被淘汰了。现今最常用的是3.5英寸的软驱，可以读写1.44MB的3.5英寸软盘,5.25英寸的软盘已经淘汰，很少会见到。软驱分内置和外置两种。内置软驱使用专用的FDD接口，而外置软驱一般用于笔记本电脑，使用USB接口。软驱有很多缺点，随着计算机的发展，这些缺点逐渐明显：容量太小，读写速度慢，软盘的寿命和可靠性差等，数据易丢失等，因此软驱已经被其他设备取代．新造的电脑都已经不再安装软驱，个人装机用户也不再安装软驱。', 'https://baike.baidu.com/item/%E8%BD%AF%E7%9B%98%E9%A9%B1%E5%8A%A8%E5%99%A8', '1', null);
INSERT INTO `t_vocabulary` VALUES ('159', 'FDDI', 'Fiber-optic Data Distribution Inter face', '光纤数据分布接口', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('160', 'FDM', 'Frequency-Division Multiplexing', '频分多路', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('161', 'FDMA', 'Frequency Division Multiple Address', '频分多址', '有许多不同技术可以用来实现信道共享。把信道频带分割为若干更窄的互不相交的频带（称为子频带），把每个子频带分给一个用户专用（称为地址），这种技术被称为“频分多址”技术。频分复用（FDM）是指载波带宽被划分为多种不同频带的子信道，每个子信道可以并行传送一路信号的一种技术。频分复用技术下，多个用户可以共享一个物理通信信道，该过程即为频分多址复用（FDMA）。FDMA 模拟传输是效率最低的网络，这主要体现在模拟信道每次只能供一个用户使用，使得带宽得不到充分利用。', 'https://baike.baidu.com/item/%E9%A2%91%E5%88%86%E5%A4%9A%E5%9D%80', '4208099247', null);
INSERT INTO `t_vocabulary` VALUES ('162', 'FEC', 'Forward Error Correction', '前向纠错', '前向纠错也叫前向纠错码(Forward Error Correction，简称FEC)，是增加数据通讯可信度的方法。在单向通讯信道中，一旦错误被发现，其接收器将无权再请求传输。FEC 是利用数据进行传输冗余信息的方法，当传输中出现错误，将允许接收器再建数据。', 'https://baike.baidu.com/item/%E5%89%8D%E5%90%91%E7%BA%A0%E9%94%99', '3441208159', null);
INSERT INTO `t_vocabulary` VALUES ('163', 'FEK', 'File Encryption Key', '文件密钥', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('164', 'FEP', 'Front Ef fect Processor', '前端处理机', '前端处理机Front－End Processor是一种通信控制器。', 'https://baike.baidu.com/item/%E5%89%8D%E7%AB%AF%E5%A4%84%E7%90%86%E6%9C%BA', '1', null);
INSERT INTO `t_vocabulary` VALUES ('165', 'FET', 'Field Effect Transistor', '场效应晶体管', '场效应晶体管（Field Effect Transistor缩写(FET)）简称场效应管。主要有两种类型：结型场效应管（junction FET—JFET)和金属 - 氧化物半导体场效应管（metal-oxide  semiconductor  FET，简称MOS-FET）。由多数载流子参与导电，也称为单极型晶体管。它属于电压控制型半导体器件。具有输入电阻高（107～1015Ω）、噪声小、功耗低、动态范围大、易于集成、没有二次击穿现象、安全工作区域宽等优点，现已成为双极型晶体管和功率晶体管的强大竞争者。场效应管（FET）是利用控制输入回路的电场效应来控制输出回路电流的一种半导体器件，并以此命名。由于它仅靠半导体中的多数载流子导电，又称单极型晶体管。FET 英文为Field Effect Transistor，简写成FET。\n[1]', 'https://baike.baidu.com/item/%E5%9C%BA%E6%95%88%E5%BA%94%E6%99%B6%E4%BD%93%E7%AE%A1', '1', null);
INSERT INTO `t_vocabulary` VALUES ('166', 'FIFO', 'First In First Out', '先进先出', '先进先出，是指根据先入库先发出的原则，对于发出的存货以先入库存货的单价计算发出存货成本的方法。采用这种方法的具体做法是：先按存货的期初余额的单价计算发出的存货的成本，领发完毕后，再按第一批入库的存货的单价计算，依此从前向后类推，计算发出存货和结存货的成本，这就是先进先出。', 'https://baike.baidu.com/item/%E5%85%88%E8%BF%9B%E5%85%88%E5%87%BA', '532643541', null);
INSERT INTO `t_vocabulary` VALUES ('167', 'FM', 'Frequency Modulation', '频率调制', '频率调制是一种以载波的瞬时频率变化来表示信息的调制方式，通过利用载波的不同频率来表达不同的信息。', 'https://baike.baidu.com/item/%E9%A2%91%E7%8E%87%E8%B0%83%E5%88%B6', '3154107743', null);
INSERT INTO `t_vocabulary` VALUES ('168', 'FPU', 'Float Point Unit', '浮点部件', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('169', 'FRC', 'Frame Rate Control', '帧频控制', 'FRC（帧频控制技术）全称Frame Rate Control 即在后续的帧刷新时显示了不同的亮度级，从而将每个基本色（R、G、B）深度扩展至8 bit（位），将颜色深度扩展至24位，使显示色彩更加丰富靓丽。', 'https://baike.baidu.com/item/%E5%B8%A7%E9%A2%91%E6%8E%A7%E5%88%B6', '3755997183', null);
INSERT INTO `t_vocabulary` VALUES ('170', 'FTAM', 'File Transfer Access and Management', '文件传输访问和管理', '文件传输访问方法(File Transfer Access Method， FTAM)也称作文件传输访问与管理或者电子文件传输访问方法(EFTAM)，是一种规定网络计算机之间传输文件方法的ISO标准(8571)。', 'https://baike.baidu.com/item/%E6%96%87%E4%BB%B6%E4%BC%A0%E8%BE%93%E8%AE%BF%E9%97%AE%E5%92%8C%E7%AE%A1%E7%90%86', '1', null);
INSERT INTO `t_vocabulary` VALUES ('171', 'FTP', 'File Transfer Protocol', '文件传输协议', '文件传输协议（File Transfer Protocol，FTP）是用于在网络上进行文件传输的一套标准协议，它工作在 OSI 模型的第七层， TCP 模型的第四层， 即应用层， 使用 TCP 传输而不是 UDP， 客户在和服务器建立连接前要经过一个“三次握手”的过程， 保证客户与服务器之间的连接是可靠的， 而且是面向连接， 为数据传输提供可靠保证。\n[1] \nFTP允许用户以文件操作的方式（如文件的增、删、改、查、传送等）与另一主机相互通信。然而， 用户并不真正登录到自己想要存取的计算机上面而成为完全用户， 可用FTP程序访问远程资源， 实现用户往返传输文件、目录管理以及访问电子邮件等等， 即使双方计算机可能配有不同的操作系统和文件存储方式。\n[1]', 'https://baike.baidu.com/item/%E6%96%87%E4%BB%B6%E4%BC%A0%E8%BE%93%E5%8D%8F%E8%AE%AE', '1', null);
INSERT INTO `t_vocabulary` VALUES ('172', 'GAL', 'General Array Logic', '通用逻辑阵列', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('173', 'GCR', 'Group-Coded Recording', '成组编码记录', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('174', 'GDI', 'Graphics Device Interface', '图形设备接口', '图形设备接口（GDI ：Graphics Device Interface），它的主要任务是负责系统与绘图程序之间的信息交换，处理所有Windows程序的图形输出。', 'https://baike.baidu.com/item/%E5%9B%BE%E5%BD%A2%E8%AE%BE%E5%A4%87%E6%8E%A5%E5%8F%A3', '3218995199', null);
INSERT INTO `t_vocabulary` VALUES ('175', 'GIF', 'Graphics Interchange Format', '一种图片文件格式,图形转换格式', 'Graphics Interchange Format(GIF)的原义是“图像互换格式”，是CompuServe公司在 1987年开发的图像文件格式。', 'https://baike.baidu.com/item/Graphics%20Interchange%20Format', '1', null);
INSERT INTO `t_vocabulary` VALUES ('176', 'GIS', 'Geographic Information System', '地理信息系统', '地理信息系统（Geographic Information System或 Geo－Information system，GIS）有时又称为“地学信息系统”。它是一种特定的十分重要的空间信息系统。它是在计算机硬、软件系统支持下，对整个或部分地球表层（包括大气层）空间中的有关地理分布数据进行采集、储存、管理、运算、分析、显示和描述的技术系统。位置与地理信息既是LBS的核心，也是LBS的基础。一个单纯的经纬度坐标只有置于特定的地理信息中，代表为某个地点、标志、方位后，才会被用户认识和理解。用户在通过相关技术获取到位置信息之后，还需要了解所处的地理环境，查询和分析环境信息，从而为用户活动提供信息支持与服务。地理信息系统（GIS，Geographic Information System）是一门综合性学科，结合地理学与地图学以及遥感和计算机科学，已经广泛的应用在不同的领域，是用于输入、存储、查询、分析和显示地理数据的计算机系统，随着GIS的发展，也有称GIS为“地理信息科学”（Geographic Information Science），近年来，也有称GIS为\"地理信息服务\"（Geographic Information service）。GIS是一种基于计算机的工具，它可以对空间信息进行分析和处理（简而言之，是对地球上存在的现象和发生的事件进行成图和分析）。 GIS 技术把地图这种独特的视觉化效果和地理分析功能与一般的数据库操作（例如查询和统计分析等）集成在一起。', 'https://baike.baidu.com/item/%E5%9C%B0%E7%90%86%E4%BF%A1%E6%81%AF%E7%B3%BB%E7%BB%9F', '1', null);
INSERT INTO `t_vocabulary` VALUES ('177', 'GPI', 'Graphical Programming Interface', '图形编程接口', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('178', 'GPIB', 'General Purpose Interface Bus', '通用接口总线', '通用接口总线（General-Purpose Interface Bus，GPIB）是一种设备和计算机连接的总线。大多数台式仪器是通过GPIB线以及GPIB接口与电脑相连。', 'https://baike.baidu.com/item/%E9%80%9A%E7%94%A8%E6%8E%A5%E5%8F%A3%E6%80%BB%E7%BA%BF', '1', null);
INSERT INTO `t_vocabulary` VALUES ('179', 'GPS', 'Global Positioning System', '全球定位系统', null, null, '1', null);
INSERT INTO `t_vocabulary` VALUES ('180', 'GSX', 'Graphics System Extension ', '图形系统扩展', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('181', 'GUI', 'Graphical User Interface', '图形用户接口', '图形用户界面（Graphical User Interface，简称 GUI，又称图形用户接口）是指采用图形方式显示的计算机操作用户界面。\n[1] \n图形用户界面是一种人与计算机通信的界面显示格式，允许用户使用鼠标等输入设备操纵屏幕上的图标或菜单选项，以选择命令、调用文件、启动程序或执行其它一些日常任务。与通过键盘输入文本或字符命令来完成例行任务的字符界面相比，图形用户界面有许多优点。图形用户界面由窗口、下拉菜单、对话框及其相应的控制机制构成，在各种新式应用程序中都是标准化的，即相同的操作总是以同样的方式来完成，在图形用户界面，用户看到和操作的都是图形对象，应用的是计算机图形学的技术。\n[1]', 'https://baike.baidu.com/item/%E5%9B%BE%E5%BD%A2%E7%94%A8%E6%88%B7%E6%8E%A5%E5%8F%A3', '1', null);
INSERT INTO `t_vocabulary` VALUES ('182', 'HPSB', 'high performance serial bus', '高性能串行总线', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('183', 'HDTV', 'high-definition television', '高清晰度电视', 'HDTV是 High Definition Television 的简称，中文意思是“高清晰度电视”，源于DTV（Digital Television）“数字电视”技术，采用数字信号，拥有最佳的视频、音频效果。HDTV有三种显示格式，分别是：720P（1280×720，非交错式，场频为24、30或60），1080 i（1920×1080，交错式，场频60），1080P（1920×1080，非交错式，场频为24或30）。', 'https://baike.baidu.com/item/%E9%AB%98%E6%B8%85%E6%99%B0%E5%BA%A6%E7%94%B5%E8%A7%86', '1', null);
INSERT INTO `t_vocabulary` VALUES ('184', 'HDC', 'Hard Disk Control', '硬盘控制器', '硬盘控制器（HardDriveController）是电脑里的控制部件，集成卡可接入多硬盘,实现硬盘多功能集合.', 'https://baike.baidu.com/item/%E7%A1%AC%E7%9B%98%E6%8E%A7%E5%88%B6%E5%99%A8', '1', null);
INSERT INTO `t_vocabulary` VALUES ('185', 'HDD', 'Hard Disk Drive', '硬盘驱动器', '硬盘驱动器（Hard-Disk Drive）简称硬盘，是一种主要的电脑存储媒介，由一个或者多个铝制或者玻璃制的碟片组成。这些碟片外覆盖有铁磁性材料。绝大多数硬盘都是固定硬盘，被永久性地密封固定在硬盘驱动器中。现在可移动硬盘越来越普及，种类也越来越多。', 'https://baike.baidu.com/item/%E7%A1%AC%E7%9B%98%E9%A9%B1%E5%8A%A8%E5%99%A8', '1', null);
INSERT INTO `t_vocabulary` VALUES ('186', 'HDLC', 'High-level Data Link Control', ' 高级数据链路控制', '高级数据链路控制（HDLC，High-level Data Link Control）是一组用于在网络结点间传送数据的协议，是由国际标准化组织（ISO）颁布的一种高可靠性、高效率的数据链路控制规程，其特点是各项数据和控制信息都以比特为单位，采用“帧”的格式传输\n[1] \n。', 'https://baike.baidu.com/item/%20%E9%AB%98%E7%BA%A7%E6%95%B0%E6%8D%AE%E9%93%BE%E8%B7%AF%E6%8E%A7%E5%88%B6', '1', null);
INSERT INTO `t_vocabulary` VALUES ('187', 'HEX', 'HEXadecimal', '十六进制', '十六进制（简写为hex或下标16）在数学中是一种逢16进1的进位制。一般用数字0到9和字母A到F（或a~f）表示，其中:A~F表示10~15，这些称作十六进制数字。', 'https://baike.baidu.com/item/%E5%8D%81%E5%85%AD%E8%BF%9B%E5%88%B6', '2413820891', null);
INSERT INTO `t_vocabulary` VALUES ('188', 'HPFS', 'High Performance File System', '高性能文件系统', '高性能文件系统 （High Performance File System ，HPFS），HPFS是Microsoft的LAN Manager中的文件系统，同时也是IBM的LAN Server和OS/2产品。在OS/2中，它就是HPFS，但是在LAN Manager和LAN Server产品中，它是HPFS386，这是HPFS的改进版本。HPFS提供了DOS文件系统中的文件分配表(FAT)所没有的长文件名和性能增强特性。另外，HPSF还能访问较大的硬盘驱动器，提供更多的组织特性并改善了文件系统的安全特性。HPFS386增加了对HPFS文件系统的32位访问，另外还增加了容错和安全性功能。', 'https://baike.baidu.com/item/%E9%AB%98%E6%80%A7%E8%83%BD%E6%96%87%E4%BB%B6%E7%B3%BB%E7%BB%9F', '1', null);
INSERT INTO `t_vocabulary` VALUES ('189', 'HPSB', 'High Performance Serial Bus', '高性能串行总线', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('190', 'HTML', 'Hyper Text Markup Language', '超文本标记语言', 'HTML称为超文本标记语言，是一种标识性的语言。它包括一系列标签．通过这些标签可以将网络上的文档格式统一，使分散的Internet资源连接为一个逻辑整体。HTML文本是由HTML命令组成的描述性文本，HTML命令可以说明文字，图形、动画、声音、表格、链接等。\n[1] \n超文本是一种组织信息的方式，它通过超级链接方法将文本中的文字、图表与其他信息媒体相关联。这些相互关联的信息媒体可能在同一文本中，也可能是其他文件，或是地理位置相距遥远的某台计算机上的文件。这种组织信息方式将分布在不同位置的信息资源用随机方式进行连接，为人们查找，检索信息提供方便。\n[1]', 'https://baike.baidu.com/item/%E8%B6%85%E6%96%87%E6%9C%AC%E6%A0%87%E8%AE%B0%E8%AF%AD%E8%A8%80', '1', null);
INSERT INTO `t_vocabulary` VALUES ('191', 'HTTP', 'Hyper Text Transport Protocol', '超文本传输协议', 'http是一个简单的请求-响应协议，它通常运行在TCP之上。它指定了客户端可能发送给服务器什么样的消息以及得到什么样的响应。请求和响应消息的头以ASCII码形式给出；而消息内容则具有一个类似MIME的格式。这个简单模型是早期Web成功的有功之臣，因为它使得开发和部署是那么的直截了当。\n[1]', 'https://baike.baidu.com/item/%E8%B6%85%E6%96%87%E6%9C%AC%E4%BC%A0%E8%BE%93%E5%8D%8F%E8%AE%AE', '1', null);
INSERT INTO `t_vocabulary` VALUES ('192', 'ISA', 'industry standard architecture', '工业标准结构体系', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('193', 'IRC', 'internet relay chat ', ' 互联网多线交谈', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('194', 'IAC ', 'Inter-Application Communications', '应用间通信', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('195', 'IC', 'Integrated Circuit', '集成电路', '集成电路（integrated circuit）是一种微型电子器件或部件。采用一定的工艺，把一个电路中所需的晶体管、电阻、电容和电感等元件及布线互连一起，制作在一小块或几小块半导体晶片或介质基片上，然后封装在一个管壳内，成为具有所需电路功能的微型结构；其中所有元件在结构上已组成一个整体，使电子元件向着微小型化、低功耗、智能化和高可靠性方面迈进了一大步。它在电路中用字母“IC”表示。集成电路发明者为杰克·基尔比（基于锗（Ge）的集成电路）和罗伯特·诺伊思（基于硅（Si）的集成电路）。当今半导体工业大多数应用的是基于硅的集成电路。是20世纪50年代后期一60年代发展起来的一种新型半导体器件。它是经过氧化、光刻、扩散、外延、蒸铝等半导体制造工艺，把构成具有一定功能的电路所需的半导体、电阻、电容等元件及它们之间的连接导线全部集成在一小块硅片上，然后焊接封装在一个管壳内的电子器件。其封装外壳有圆壳式、扁平式或双列直插式等多种形式。集成电路技术包括芯片制造技术与设计技术，主要体现在加工设备，加工工艺，封装测试，批量生产及设计创新的能力上。', 'https://baike.baidu.com/item/%E9%9B%86%E6%88%90%E7%94%B5%E8%B7%AF', '4148080187', null);
INSERT INTO `t_vocabulary` VALUES ('196', 'ICMP', 'Internet Control Mes sage Protocol', '因特网控制消息协议', 'ICMP是“InternetControlMessageProtocol”（因特网控制消息协议）的缩写。它是TCP/IP协议族的一个子协议，用于在IP主机、路由器之间传递控制消息。控制消息是指网络通不通、主机是否可达、路由是否可用等网络本身的消息。这些控制消息虽然并不传输用户数据，但是对于用户数据的传递起着重要的作用。', 'https://baike.baidu.com/item/%E5%9B%A0%E7%89%B9%E7%BD%91%E6%8E%A7%E5%88%B6%E6%B6%88%E6%81%AF%E5%8D%8F%E8%AE%AE', '1', null);
INSERT INTO `t_vocabulary` VALUES ('197', 'ICP', 'Internet Content Provider', '因特网内容服务提供商,是 ISP中提供信息服务的一种机构', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('198', 'IDC', 'International Development Center', '国际开发中心', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('199', 'IDE', ' Integrated Development Environment ', '集成开发环境', '集成开发环境（IDE，Integrated Development Environment ）是用于提供程序开发环境的应用程序，一般包括代码编辑器、编译器、调试器和图形用户界面等工具。集成了代码编写功能、分析功能、编译功能、调试功能等一体化的开发软件服务套。所有具备这一特性的软件或者软件套（组）都可以叫集成开发环境。如微软的Visual Studio系列，Borland的C++ Builder、Delphi系列等。该程序可以独立运行，也可以和其它程序并用。IDE多被用于开发HTML应用软件。例如，许多人在设计网站时使用IDE（如HomeSite、DreamWeaver等），因为很多项任务会自动生成。', 'https://baike.baidu.com/item/%E9%9B%86%E6%88%90%E5%BC%80%E5%8F%91%E7%8E%AF%E5%A2%83', '1', null);
INSERT INTO `t_vocabulary` VALUES ('200', 'IDL', 'Interface Definition Language', '接口定义语言', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('201', 'IEEE', 'Institute of Electrical and Electronics Engineering', '电子电器工程师协会', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('202', 'IGP', 'Interior Gateway Protocol', '内部网关协议', 'IGP（内部网关协议）是在一个自治网络内网关（主机和路由器）间交换路由信息的协议。路由信息能用于网间协议（IP）或者其它网络协议来说明路由传送是如何进行的。IGP协议包括RIP、OSPF、IS-IS、IGRP、EIGRP。', 'https://baike.baidu.com/item/%E5%86%85%E9%83%A8%E7%BD%91%E5%85%B3%E5%8D%8F%E8%AE%AE', '1', null);
INSERT INTO `t_vocabulary` VALUES ('203', 'IIS', 'Internet Information Service', '因特网信息服务', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('204', 'IP', 'Internet Protocol', '因特网协议', 'IP是英文Internet Protocol的缩写，意思是“网络之间互连的协议”，也就是为计算机网络相互连接进行通信而设计的协议。在因特网中，它是能使连接到网上的所有计算机网络实现相互通信的一套规则，规定了计算机在因特网上进行通信时应当遵守的规则。', 'https://baike.baidu.com/item/%E5%9B%A0%E7%89%B9%E7%BD%91%E5%8D%8F%E8%AE%AE', '1', null);
INSERT INTO `t_vocabulary` VALUES ('205', 'IPC', 'Inter-Process Communication', '进程间通信', '进程间通信就是在不同进程之间传播或交换信息，那么不同进程之间存在着什么双方都可以访问的介质呢？进程的用户空间是互相独立的，一般而言是不能互相访问的，唯一的例外是共享内存区。另外，系统空间是“公共场所”，各进程均可以访问，所以内核也可以提供这样的条件。此外，还有双方都可以访问的外设。在这个意义上，两个进程当然也可以通过磁盘上的普通文件交换信息，或者通过“注册表”或其它数据库中的某些表项和记录交换信息。广义上这也是进程间通信的手段，但是一般都不把这算作“进程间通信”。', 'https://baike.baidu.com/item/%E8%BF%9B%E7%A8%8B%E9%97%B4%E9%80%9A%E4%BF%A1', '1', null);
INSERT INTO `t_vocabulary` VALUES ('206', 'IPX', 'Internet Packer Exchitecture', '互联网报文分组交换', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('207', 'ISA', 'Industry Standard Architecture', '工业标准结构,是 IBM PC/ XT总线标准', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('208', 'ISDN', 'Integrated Service Digital Network', '综合业务数字网', '综合业务数字网（Integrated Services Digital Network，ISDN）是一个数字电话网络国际标准，是一种典型的电路交换网络系统。在ITU的建议中，ISDN是一种在数字电话网IDN的基础上发展起来的通信网络，ISDN能够支持多种业务，包括电话业务和非电话业务。', 'https://baike.baidu.com/item/%E7%BB%BC%E5%90%88%E4%B8%9A%E5%8A%A1%E6%95%B0%E5%AD%97%E7%BD%91', '1', null);
INSERT INTO `t_vocabulary` VALUES ('209', 'ISO', 'International Standard Organization', '国际标准化组织', '国际标准化组织（International Organization for Standardization，ISO）简称ISO，是一个全球性的非政府组织，是国际标准化领域中一个十分重要的组织。ISO一来源于希腊语“ISOS”，即“EQUAL”——平等之意。ISO国际标准组织成立于1947年，中国是ISO的正式成员，代表中国参加ISO的国家机构是中国国家技术监督局（CSBTS）。\n[1] \nISO负责目前绝大部分领域（包括军工、石油、船舶等垄断行业）的标准化活动。ISO现有117个成员，包括117个国家和地区。ISO的最高权力机构是每年一次的“全体大会”，其日常办事机构是中央秘书处，设在瑞士日内瓦。中央秘书处现有170名职员，由秘书长领导。ISO的宗旨是“在世界上促进标准化及其相关活动的发展，以便于商品和服务的国际交换，在智力、科学、技术和经济领域开展合作。”ISO通过它的2856个技术结构开展技术活动，其中技术委员会（简称SC）共611个，工作组（WG）2022个，特别工作组38个。中国于1978年加入ISO，在2008年10月的第31届国际化标准组织大会上，中国正式成为ISO的常任理事国。国际标准化组织总部设于瑞士日内瓦，成员包括162个会员国。该组织自我定义为非政府组织，官方语言是英语、法语和俄语。参加者包括各会员国的国家标准机构和主要公司。它是世界上最大的非政府性标准化专门机构，是国际标准化领域中一个十分重要的组织。', 'https://baike.baidu.com/item/%E5%9B%BD%E9%99%85%E6%A0%87%E5%87%86%E5%8C%96%E7%BB%84%E7%BB%87', '1', null);
INSERT INTO `t_vocabulary` VALUES ('210', 'ISP', 'Internet Service Provider', '因特网服务提供者', '互联网服务提供商（Internet Service Provider），简称ISP，指的是面向公众提供下列信息服务的经营者：一是接入服务，即帮助用户接入Internet；二是导航服务，即帮助用户在Internet上找到所需要的信息；三是信息服务，即建立数据服务系统，收集、加工、存储信息，定期维护更新，并通过网络向用户提供信息内容服务。', 'https://baike.baidu.com/item/%E5%9B%A0%E7%89%B9%E7%BD%91%E6%9C%8D%E5%8A%A1%E6%8F%90%E4%BE%9B%E8%80%85', '1', null);
INSERT INTO `t_vocabulary` VALUES ('211', 'ITU', 'International Telecom Union', '国际电信联盟', '国际电信联盟是联合国的一个重要专门机构，也是联合国机构中历史最长的一个国际组织。简称“国际电联”、“电联”或“ITU”。国际电联是主管信息通信技术事务的联合国机构，负责分配和管理全球无线电频谱与卫星轨道资源，制定全球电信标准，向发展中国家提供电信援助，促进全球电信发展。作为世界范围内联系各国政府和私营部门的纽带，国际电联通过其麾下的无线电通信、标准化和发展电信展览活动，而且是信息社会世界高峰会议的主办机构。国际电联总部设于瑞士日内瓦，其成员包括193个成员国和700多个部门成员及部门准成员和学术成员。每年的5月17日是世界电信日(World Telecommunication Day)。2014年10月23日，赵厚麟当选国际电信联盟新一任秘书长，成为国际电信联盟150年历史上首位中国籍秘书长，已于2015年1月1日正式上任，任期四年。\n[1]', 'https://baike.baidu.com/item/%E5%9B%BD%E9%99%85%E7%94%B5%E4%BF%A1%E8%81%94%E7%9B%9F', '1', null);
INSERT INTO `t_vocabulary` VALUES ('212', 'JDBC', 'Java Database Connectivity', 'J ava数据库互联', 'Java数据库连接，（Java Database Connectivity，简称JDBC）是Java语言中用来规范客户端程序如何来访问数据库的应用程序接口，提供了诸如查询和更新数据库中数据的方法。JDBC也是Sun Microsystems的商标。我们通常说的JDBC是面向关系型数据库的。', 'https://baike.baidu.com/item/Java%20Database%20Connectivity', '1', null);
INSERT INTO `t_vocabulary` VALUES ('213', 'JPEG', 'Joint Photographic Experts Group', '联合图片专家组', 'Joint Photographic Experts Group（联合图像专家小组）,缩写JPEG。JPEG的压缩方式通常是破坏性资料压缩（lossy compression），意即在压缩过程中图像的品质会遭受到可见的破坏，有一种以JPEG为基础的标准Progressive JPEG是采用无失真的压缩方式，但Progressive JPEG并没有受到广泛的支援。', 'https://baike.baidu.com/item/Joint%20Photographic%20Experts%20Group', '1', null);
INSERT INTO `t_vocabulary` VALUES ('214', 'JSP', 'Java Server Page', 'Java 服务器页面技术', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('215', 'JVM', 'Java Virtual Machine ', 'Java虚拟机', '虚拟机是一种抽象化的计算机，通过在实际的计算机上仿真模拟各种计算机功能来实现的。Java虚拟机有自己完善的硬体架构，如处理器、堆栈、寄存器等，还具有相应的指令系统。Java虚拟机屏蔽了与具体操作系统平台相关的信息，使得Java程序只需生成在Java虚拟机上运行的目标代码（字节码），就可以在多种平台上不加修改地运行。', 'https://baike.baidu.com/item/Java%E8%99%9A%E6%8B%9F%E6%9C%BA', '1', null);
INSERT INTO `t_vocabulary` VALUES ('216', 'KB', 'Kilobyte', '千字节', '千字节（Kbytes），计算机存储容量单位，也常用KB来表示。1KB=1024字节。', 'https://baike.baidu.com/item/%E5%8D%83%E5%AD%97%E8%8A%82', '2122234605', null);
INSERT INTO `t_vocabulary` VALUES ('217', 'KS', 'keyword search', '关键字搜索', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('218', 'KBPS', 'Kilobits Per Second', '每秒千比特', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('219', 'KMS', 'Knowledge Management System', '知识管理系统\r\n', '知识管理系统（英语：Knowledge management system），是收集、处理、分享一个组织的全部知识的信息系统，通常有计算机系统支持。利用软件系统或其他工具，对组织中大量的有价值的方案、策划、成果、经验等知识进行分类存储和管理，积累知识资产避免流失，促进知识的学习、共享、培训、再利用和创新，有效降低组织运营成本，强化其核心竞争力的管理方法。', 'https://baike.baidu.com/item/%E7%9F%A5%E8%AF%86%E7%AE%A1%E7%90%86%E7%B3%BB%E7%BB%9F%0D%0A', '1', null);
INSERT INTO `t_vocabulary` VALUES ('220', 'LCD', 'liquid crystal display monitor', '液晶显示器', '液晶显示器是一种借助于薄膜晶体管(TFT)驱动的有源矩阵液晶显示器，它主要是以电流刺激液晶分子产生点、线、面配合背部灯管构成画面。IPS、TFT、SLCD都属于LCD的子类。\n[1] \n液晶显示器的工作原理是：在电场的作用下，利用液晶分子的排列方向发生变化，使外光源透光率改变（调制），完成电一光变换，再利用R、G、B三基色信号的不同激励，通过红、绿、蓝三基色滤光膜，完成时域和空间域的彩色重显。\n[1]', 'https://baike.baidu.com/item/%E6%B6%B2%E6%99%B6%E6%98%BE%E7%A4%BA%E5%99%A8', '1', null);
INSERT INTO `t_vocabulary` VALUES ('221', 'LAN', 'Local Area Network', '局域网', '局域网的覆盖范围一般是方圆几千米之内，其具备的安装便捷、成本节约、扩展方便等特点使其在各类办公室内运用广泛。局域网可以实现文件管理、应用软件共享、打印机共享等功能，在使用过程当中，通过维护局域网网络安全，能够有效地保护资料安全，保证局域网网络能够正常稳定的运行。\n[1]', 'https://baike.baidu.com/item/%E5%B1%80%E5%9F%9F%E7%BD%91', '3220176893', null);
INSERT INTO `t_vocabulary` VALUES ('222', 'LBA ', 'Logical Block Addressing', '逻辑块寻址', '逻辑块寻址（logical block addressing）是允许计算机访问一个大于528 兆字节的硬盘的技术。', 'https://baike.baidu.com/item/%E9%80%BB%E8%BE%91%E5%9D%97%E5%AF%BB%E5%9D%80', '1', null);
INSERT INTO `t_vocabulary` VALUES ('223', 'LDT', 'Logic Design Translator', '逻辑设计翻译程序', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('224', 'LED', 'Light Emitting Diode', '发光二极管', '发光二极管简称为LED。由含镓（Ga）、砷（As）、磷（P）、氮（N）等的化合物制成。当电子与空穴复合时能辐射出可见光，因而可以用来制成发光二极管。在电路及仪器中作为指示灯，或者组成文字或数字显示。砷化镓二极管发红光，磷化镓二极管发绿光，碳化硅二极管发黄光，氮化镓二极管发蓝光。因化学性质又分有机发光二极管OLED和无机发光二极管LED。', 'https://baike.baidu.com/item/%E5%8F%91%E5%85%89%E4%BA%8C%E6%9E%81%E7%AE%A1', '1', null);
INSERT INTO `t_vocabulary` VALUES ('225', 'LIFO', 'Last In First Out', '后进先出', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('226', 'LP', 'Linear Programming', '线性规划', '线性规划（Linear programming,简称LP），是运筹学中研究较早、发展较快、应用广泛、方法较成熟的一个重要分支，它是辅助人们进行科学管理的一种数学方法。研究线性约束条件下线性目标函数的极值问题的数学理论和方法。英文缩写LP。线性规划是运筹学的一个重要分支，广泛应用于军事作战、经济分析、经营管理和工程技术等方面。为合理地利用有限的人力、物力、财力等资源作出的最优决策，提供科学的依据。', 'https://baike.baidu.com/item/%E7%BA%BF%E6%80%A7%E8%A7%84%E5%88%92', '1470627831', null);
INSERT INTO `t_vocabulary` VALUES ('227', 'LPC', ' Local Procedure Call', '局部过程调用', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('228', 'LSIC', 'Large Scale Integration Circuit', '大规模集成电路', '大规模集成电路：LSI (Large Scale Integration )，通常指含逻辑门数为100门～9999门（或含元件数1000个～99999个），在一个芯片上集合有1000个以上电子元件的集成电路。集成电路（integrated circuit，港台称之为积体电路）是一种微型电子器件或部件。采用一定的工艺，把一个电路中所需的晶体管、二极管、电阻、电容和电感等元件及布线互连一起，制作在一小块或几小块半导体晶片或介质基片上，然后封装在一个管壳内，成为具有所需电路功能的微型结构；其中所有元件在结构上已组成一个整体。可用字母“IC”（也有用文字符号“N”等）表示。\n[1]', 'https://baike.baidu.com/item/%E5%A4%A7%E8%A7%84%E6%A8%A1%E9%9B%86%E6%88%90%E7%94%B5%E8%B7%AF', '1', null);
INSERT INTO `t_vocabulary` VALUES ('229', 'MAN', 'Metropolitan area network', '城域网', '城域网(Metropolitan Area Network)是在一个城市范围内所建立的计算机通信网，简称MAN。属宽带局域网。由于采用具有有源交换元件的局域网技术，网中传输时延较小，它的传输媒介主要采用光缆，传输速率在100兆比特/秒以上。MAN的一个重要用途是用作骨干网，通过它将位于同一城市内不同地点的主机、数据库，以及LAN等互相联接起来，这与WAN的作用有相似之处，但两者在实现方法与性能上有很大差别。基于一种大型的LAN，通常使用与LAN相似的技术。MAN单独的列出的一个主要原因是已经有了一个标准：分布式队列双总线DQDB（Distributed Queue Dual Bus），即IEEE802.6。DQDB是由双总线构成，所有的计算机都连结在上面。', 'https://baike.baidu.com/item/%E5%9F%8E%E5%9F%9F%E7%BD%91', '2289801883', null);
INSERT INTO `t_vocabulary` VALUES ('230', 'MICR', 'magnetic-ink character recognition', '磁墨水字符识别器', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('231', 'MAC', 'Medium Access Control', '介质访问控制', '介质访问控制(medium access control)简称MAC。 是解决当局域网中共用信道的使用产生竞争时，如何分配信道的使用权问题。它定义了数据帧怎样在介质上进行传输。在共享同一个带宽的链路中，对连接介质的访问是“先来先服务”的。物理寻址在此处被定义，逻辑拓扑（信号通过物理拓扑的路径）也在此处被定义。线路控制、出错通知（不纠正）、帧的传递顺序和可选择的流量控制也在这一子层实现。', 'https://baike.baidu.com/item/%E4%BB%8B%E8%B4%A8%E8%AE%BF%E9%97%AE%E6%8E%A7%E5%88%B6', '1', null);
INSERT INTO `t_vocabulary` VALUES ('232', 'MBR', 'Master Boot Record', '主引导记录', '主引导记录（MBR，Master Boot Record）是位于磁盘最前边的一段引导（Loader）代码。它负责磁盘操作系统(DOS)对磁盘进行读写时分区合法性的判别、分区引导信息的定位，它由磁盘操作系统(DOS)在对硬盘进行初始化时产生的。', 'https://baike.baidu.com/item/%E4%B8%BB%E5%BC%95%E5%AF%BC%E8%AE%B0%E5%BD%95', '1', null);
INSERT INTO `t_vocabulary` VALUES ('233', 'MC', ' Memory Card', '存储卡片', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('234', 'MCA', 'Micro Channel Architecture', '微通道结构', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('235', 'MDA', 'Monochrome Display Adapter', '单色显示适配卡', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('236', 'MFM', 'Modified Frequency Modulation', '改进调频制', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('237', 'MIB', 'Management Information Bass', '管理信息库', '管理信息库是指所监控网络设备的标准变量定义的集合。为了在SNMP报文中唯一标识设备中的管理变量，SNMP用层次结构命名方案来识别管理对象，就像一棵树，树的节点表示管理对象，可以用从根开始的一条路径来进行无二义的识别。\n[1]', 'https://baike.baidu.com/item/%E7%AE%A1%E7%90%86%E4%BF%A1%E6%81%AF%E5%BA%93', '1', null);
INSERT INTO `t_vocabulary` VALUES ('238', 'MIMD ', 'Multiple Instruction Stream,Multiple Data Stream', '多指令流,多数据流', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('239', 'MIPS', 'Million Instructions Per Second', '每秒百万条指令', '每秒百万条指令（MIPS，million instructions per second）是计算处理性能的一个常用量度，表示一台大型电脑能完成的工作量。', 'https://baike.baidu.com/item/%E6%AF%8F%E7%A7%92%E7%99%BE%E4%B8%87%E6%9D%A1%E6%8C%87%E4%BB%A4', '1', null);
INSERT INTO `t_vocabulary` VALUES ('240', 'MIS', 'Management Information System', '管理信息系统', '管理信息系统（Management Information System，简称MIS）是一个以人为主导，利用计算机硬件、软件、网络通信设备以及其他办公设备，进行信息的收集、传输、加工、储存、更新、拓展和维护的系统。', 'https://baike.baidu.com/item/%E7%AE%A1%E7%90%86%E4%BF%A1%E6%81%AF%E7%B3%BB%E7%BB%9F', '1', null);
INSERT INTO `t_vocabulary` VALUES ('241', 'MISD', 'Multiple Instruction Stream,Single Data Stream', '多指令流,单数据流', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('242', 'MMDS', 'Multi-channel Multipoint Distribution Service', '多波段多点分发服务器', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('243', 'MMU', 'Memory Management Unit', '内存管理单元', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('244', 'MPC', 'Multimedia PC', '多媒体计算机', '多媒体计算机 【multimedia computer】 能够对声音、图像、视频等多媒体信息进行综合处理的计算机。多媒体计算机一般指多媒体个人计算机（MPC）。', 'https://baike.baidu.com/item/%E5%A4%9A%E5%AA%92%E4%BD%93%E8%AE%A1%E7%AE%97%E6%9C%BA', '1', null);
INSERT INTO `t_vocabulary` VALUES ('245', 'MPEG', 'Moving Picture Expert Group', '一种视频和音频的国际标准格式', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('246', 'MPLS', 'Multi-Protocol Label Switching', '多协议标记交换', '多协议标记交换是指支持多种网络层和链路层协议，采用标记替换方式的交换技术。用于无连接网络层与面向连接快速转发链路层的结合。标记是固定长度的短标识，标记替换是指输入分组中的输入标记在转发时由输出标记所替代。为此要通过预先建立的转发表中输人标记与输出标记之间的映射，从输人标记快速检索到输出标记、输出端口和下一跳地址，从而实现快速的分组转发。建立转发表的映射关系，由MPLS节点控制部件确定标记与路由之间的捆绑关系，通过IETF制定的标记分发协议(LDP)来实现。标记分发有自发下游分配和按需下游分配方式。\n[1]', 'https://baike.baidu.com/item/%E5%A4%9A%E5%8D%8F%E8%AE%AE%E6%A0%87%E8%AE%B0%E4%BA%A4%E6%8D%A2', '1', null);
INSERT INTO `t_vocabulary` VALUES ('247', 'MPS', 'Micro Processor System', '微处理器系列', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('248', 'MTBF', 'Mean Time Between Failures', '平均故障间隔时间', '平均故障间隔时间，是指产品或系统在两相邻故障间隔期内正确工作的平均时间，也称平均无故障工作时间。它是标志产品或系统能平均工作多长时间的量，用MTBF表示。\n[1] \nMTBF，平均故障间隔时间又称平均无故障时间，英文全称是“Mean Time Between Failure，是衡量一个产品(尤其是电器产品)的可靠性指标，单位为“小时”。', 'https://baike.baidu.com/item/%E5%B9%B3%E5%9D%87%E6%95%85%E9%9A%9C%E9%97%B4%E9%9A%94%E6%97%B6%E9%97%B4', '1', null);
INSERT INTO `t_vocabulary` VALUES ('249', 'MUD', 'Multiple User Dimension', '多用户空间', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('250', 'NOS', 'Network operation system', '网络操作系统', '网络操作系统 ，是一种能代替操作系统的软件程序，是网络的心脏和灵魂，是向网络计算机提供服务的特殊的操作系统。借由网络达到互相传递数据与各种消息，分为服务器（Server）及客户端（Client）。\n[1] \n而服务器的主要功能是管理服务器和网络上的各种资源和网络设备的共用，加以统合并控管流量，避免有瘫痪的可能性，而客户端就是有着能接收服务器所传递的数据来运用的功能，好让客户端可以清楚的搜索所需的资源。\n[2]', 'https://baike.baidu.com/item/%E7%BD%91%E7%BB%9C%E6%93%8D%E4%BD%9C%E7%B3%BB%E7%BB%9F', '1', null);
INSERT INTO `t_vocabulary` VALUES ('251', 'NAOC', 'No-Account Over Clock', '无效超频', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('252', 'NAT', 'Network Address Translation', '网络地址转换', '在计算器网络中，网络地址转换（Network Address Translation，缩写为NAT），也叫做网络掩蔽或者IP掩蔽（IP masquerading），是一种在IP数据包通过路由器或防火墙时重写来源IP地址或目的IP地址的技术。这种技术被普遍使用在有多台主机但只通过一个公有IP地址访问因特网的私有网络中。它是一个方便且得到了广泛应用的技术。当然，NAT也让主机之间的通信变得复杂，导致了通信效率的降低。', 'https://baike.baidu.com/item/%E7%BD%91%E7%BB%9C%E5%9C%B0%E5%9D%80%E8%BD%AC%E6%8D%A2', '1', null);
INSERT INTO `t_vocabulary` VALUES ('253', 'NC', 'Network Computer', '网络计算机', '网络计算机是受客户计算模式下的一种交互式信息设备,其上的典型应用程序行为分析对于处理器设计、系统开发有着重要意义。网络计算机用来在网络上使用的计算机，但去掉了传统的硬盘、软盘等部件，属于瘦形PC，由服务器提供网络上的程序或存储。网络计算机具有自己的处理能力，但除核心软件之外，其他软件都需从网络服务器下载，节省了频繁的软件升级和维护，也降低了成本。', 'https://baike.baidu.com/item/%E7%BD%91%E7%BB%9C%E8%AE%A1%E7%AE%97%E6%9C%BA', '1', null);
INSERT INTO `t_vocabulary` VALUES ('254', 'NDIS', 'Network Device Interface Speci fication', '网络设备接口规范', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('255', 'NCM', 'Neural Cognitive Maps', '神经元认知图', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('256', 'NFS', 'Network File System', '网络文件系统', '网络文件系统，英文Network File System(NFS)，是由SUN公司研制的UNIX表示层协议(presentation layer protocol)，能使使用者访问网络上别处的文件就像在使用自己的计算机一样。\n[1]', 'https://baike.baidu.com/item/%E7%BD%91%E7%BB%9C%E6%96%87%E4%BB%B6%E7%B3%BB%E7%BB%9F', '1', '');
INSERT INTO `t_vocabulary` VALUES ('257', 'NIS', 'Network Information Services', '网络信息服务', '网络信息服务是指以提供信息内容为主要手段的因特网信息业务。网络信息服务以网络信息查询为基础。网络信息查询的特异性在于网络环境引起的资源分布化和数字技术带来的信息资源多媒体化。\n[1]', 'https://baike.baidu.com/item/%E7%BD%91%E7%BB%9C%E4%BF%A1%E6%81%AF%E6%9C%8D%E5%8A%A1', '1', null);
INSERT INTO `t_vocabulary` VALUES ('258', 'NNTP', 'Network News Transfer Protocol', '网络新闻传输协议', '网络新闻传输协议是一个主要用于阅读和张贴新闻文章(俗称为“帖子”，比较正式的是“新闻组邮件”)到Usenet上的Internet应用协议，也负责新闻在服务器间的传送。NNTP用于向Internet上NNTP服务器或NNTP客户（新闻阅读器）发布网络新闻邮件的协议，提供通过Internet使用可靠的基于流的新闻传输，提供新闻的分发、查询、检索和投递。NNTP还专门设计用于将新闻文章保存在中心数据库的服务器上，这样用户可以选择要阅读的特定条目，还提供过期新闻的索引、交叉引用和终止。', 'https://baike.baidu.com/item/%E7%BD%91%E7%BB%9C%E6%96%B0%E9%97%BB%E4%BC%A0%E8%BE%93%E5%8D%8F%E8%AE%AE', '1', null);
INSERT INTO `t_vocabulary` VALUES ('259', 'NOC', 'Network Operations Center ', '网络操作中心', '网络操作中心（NOC，Network Operation Center）是管理、监控和维护电信网络的地方。', 'https://baike.baidu.com/item/%E7%BD%91%E7%BB%9C%E6%93%8D%E4%BD%9C%E4%B8%AD%E5%BF%83', '1', '');
INSERT INTO `t_vocabulary` VALUES ('260', 'NSP', 'Name Server Protocol', '名字服务器协议', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('261', 'NTP', 'Network Time Protocol', '网络时间协议', '网络时间协议，英文名称：Network Time Protocol（NTP）是用来使计算机时间同步化的一种协议，它可以使计算机对其服务器或时钟源（如石英钟，GPS等等)做同步化，它可以提供高精准度的时间校正（LAN上与标准间差小于1毫秒，WAN上几十毫秒），且可介由加密确认的方式来防止恶毒的协议攻击。NTP的目的是在无序的Internet环境中提供精确和健壮的时间服务。', 'https://baike.baidu.com/item/%E7%BD%91%E7%BB%9C%E6%97%B6%E9%97%B4%E5%8D%8F%E8%AE%AE', '1', null);
INSERT INTO `t_vocabulary` VALUES ('262', 'NUI', ' network user identification', '网络用户标识', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('263', 'OS', 'Operation System', '操作系统', '操作系统是管理计算机硬件与软件资源的计算机程序，同时也是计算机系统的内核与基石。操作系统需要处理如管理与配置内存、决定系统资源供需的优先次序、控制输入设备与输出设备、操作网络与管理文件系统等基本事务。操作系统也提供一个让用户与系统交互的操作界面。', 'https://baike.baidu.com/item/%E6%93%8D%E4%BD%9C%E7%B3%BB%E7%BB%9F', '4294964983', null);
INSERT INTO `t_vocabulary` VALUES ('264', 'OE', 'Object embedding', '对象嵌入', '嵌入对象：包含在源文件中并且插入目标文件中的信息（对象）。一旦嵌入，该对象成为目标文件的一部分。对嵌入对象所做的更改反映在目标文件中。在MicrosoftWord文档中，是指直接从插入点放置到文字中的图形或其他对象。  在POWERPOINT中使用WINDOWS对象链接和嵌入（OLE）技术实现应用程序之间的资源共享。  链接和嵌入对象的主要差别是存放数据的方式，以及放到目标文件后的更新方式。对象链接后，只有更改了源文件时，数据才会被更新，链接的数据保存在源文件中，目标文件中只保存源文件的位置，并显示一个链接数据的标记，如果不希望文件过大，可使用链接对象。', 'https://baike.baidu.com/item/%E5%AF%B9%E8%B1%A1%E5%B5%8C%E5%85%A5', '1534449567', null);
INSERT INTO `t_vocabulary` VALUES ('265', 'OL', 'Object linking ', '目标链接', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('266', 'OA', 'Open architecture', '开放式体系结构', '开放式体系结构 （open system architecture）具有应用系统的可移植性和可剪裁性、网络上各结点机间的互操作性和易于从多方获得软件的体系结构。简称开放 结构(OA)。它是构成开放应用体系结构（OAA)的技术基础。', 'https://baike.baidu.com/item/%E5%BC%80%E6%94%BE%E5%BC%8F%E4%BD%93%E7%B3%BB%E7%BB%93%E6%9E%84', '1', null);
