INSERT INTO `t_vocabulary` VALUES ('274', 'OLE', 'Object Linking and Embedding', '对象链接与嵌入', '组件对象使用的一种技术', 'https://baike.baidu.com/item/%E5%AF%B9%E8%B1%A1%E9%93%BE%E6%8E%A5%E4%B8%8E%E5%B5%8C%E5%85%A5', '1', null);
INSERT INTO `t_vocabulary` VALUES ('275', 'OMG', 'Object Management Group', '对象管理组织', '对象管理组织（英文Object Management Group,缩写为OMG）是一个国际协会，开始的目的是为分布式面向对象系统建立标准，现在致力于建立对程序、系统 和 业务流程建模的标准，以及基于模型的标准。OMG是一个国际性的非盈利协会。任何组织都可以加入，在缴纳会费后可以参与OMG标准的制定和投票讨论。OMG只提供标准的详细说明书，并不提供对于标准的实现软件。但是在一个标准的草案被接纳成为正式标准之前，提交这个草案并被接受的成员必须保证，他们在一年之内将会开发一个服从这个标准的产品。这样就阻止了没有参照实现的标准。OMG鼓励其他的企业或开源组织开发服从OMG标准的产品，并且设法开发强制机制，让不同的软件具有通用性。OMG的广泛使用的标准包括UML,MOF，XMI 业务流程建模标记法(BPMN)等。', 'https://baike.baidu.com/item/%E5%AF%B9%E8%B1%A1%E7%AE%A1%E7%90%86%E7%BB%84%E7%BB%87', '1', null);
INSERT INTO `t_vocabulary` VALUES ('276', 'OMR', 'Optical-Mark Recognition', '光标阅读器', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('277', 'OOM', 'Object Oriented Method', '面向对象方法', '面向对象方法(Object-Oriented Method)是一种把面向对象的思想应用于软件开发过程中，指导开发活动的系统方法，简称OO (Object-Oriented)方法，是建立在“对象”概念基础上的方法学。对象是由数据和允许的操作组成的封装体，与客观实体有直接对应关系，一个对象类定义了具有相似性质的一组对象。而每继承性是对具有层次关系的类的属性和操作进行共享的一种方式。所谓面向对象就是基于对象概念，以对象为中心，以类和继承为构造机制，来认识、理解、刻画客观世界和设计、构建相应的软件系统。', 'https://baike.baidu.com/item/%E9%9D%A2%E5%90%91%E5%AF%B9%E8%B1%A1%E6%96%B9%E6%B3%95', '1', null);
INSERT INTO `t_vocabulary` VALUES ('278', 'OOP', 'Object Oriented Programming', '面向对象程序设计', '面向对象程序设计(Object Oriented Programming)作为一种新方法，其本质是以建立模型体现出来的抽象思维过程和面向对象的方法。模型是用来反映现实世界中事物特征的。任何一个模型都不可能反映客观事物的一切具体特征，只能对事物特征和变化规律的一种抽象，且在它所涉及的范围内更普遍、更集中、更深刻地描述客体的特征。通过建立模型而达到的抽象是人们对客体认识的深化。', 'https://baike.baidu.com/item/%E9%9D%A2%E5%90%91%E5%AF%B9%E8%B1%A1%E7%A8%8B%E5%BA%8F%E8%AE%BE%E8%AE%A1', '1', null);
INSERT INTO `t_vocabulary` VALUES ('279', 'ORB', ' Object Request Broker', '对象请求代理', '对象请求代理（ORB，Object Request Broker）是对象之间建立客户端/服务端（Client/Server）关系的中间件。使用ORB，客户可以透明地调用一个服务对象上的方法，这个服务对象可以在本地，也可以在通过网络连接的其他机器上。ORB截获这一调用，同时负责查找实现服务的对象并向其传递参数、调用方法并返回最终结果。客户并不知道服务对象位于什么地方、它的编程语言和操作系统是什么，也不知道不属于对象接口的其他系统部分。这样，ORB在异构分布环境下为不同机器上的应用提供了互操作性，并无缝地集成了多种对象系统。对象请求代理（Object Request Broker）是用户提供与其他分布式网络环境中对象通信的接口，是中间件（Middleware）中主要的一类，它在CORBA(Common Object Request Broker Architecture)规范中处于核心地位，定义异构环境下对象透明地发送请求和接收响应的基本机制，是建立对象之间client/server关系的中间件。它的作用在于提供一个通信框架，透明地在异构的分布计算环境中传递对象请求。CORBA规范包括了ORB的所有标准接口。对象请求代理这个结构不需要作为组件单独实现，它由接口定义。任何提供正确接口的ORB实现都是可被接受的。', 'https://baike.baidu.com/item/%E5%AF%B9%E8%B1%A1%E8%AF%B7%E6%B1%82%E4%BB%A3%E7%90%86', '1', null);
INSERT INTO `t_vocabulary` VALUES ('280', 'OSI', 'Open System Interconnect Reference Model ', ' 开放式系统互联参考模型', null, null, null, null);
INSERT INTO `t_vocabulary` VALUES ('281', 'OSPF', 'Open Shortest Path First', '开发最短路径优先', 'OSPF协议是IETF提出的一个使用链路状态算法实现内部网关协议的标准（RFC2328，OSPFv2），现已成为域间路由协议的主流和其他路由协议的替代。OSPF支持负载均衡和基于服务类型的选路，也支持多种路由形式，如特定主机路由和子网路由等。', 'https://baike.baidu.com/item/Open%20Shortest%20Path%20First', '1', null);
INSERT INTO `t_vocabulary` VALUES ('282', 'PDA', 'personal digital assistant', '个人数字助理', '个人数字助理是是集电子记事本、便携式电脑移动通讯装置和为一体的电子产品，即将个人平常所需的资料数字化，能被广泛的利用与传输。狭义的PDA是指电子记事本，其功能较为单一，主要是管理个人信息，如通讯录、记事和备忘、日程安排、便笺、计算器、录音和辞典等功能。广义的PDA主要指掌上电脑，当然也包括其他具有类似功能的小型数字化设备。', 'https://baike.baidu.com/item/%E4%B8%AA%E4%BA%BA%E6%95%B0%E5%AD%97%E5%8A%A9%E7%90%86', '1', null);
