INSERT INTO `t_vocabulary` VALUES ('401', 'WAP', 'Wireles s Application Protocol', '无线应用协议', '无线应用协议（WAP）是于1998年初公布的，让人们使用手机等移动通信终端设备，通过WAP协议接收各种信息，上网，浏览网页，收发电子邮件，甚至进行网上电子商务的一项网络通信协。', 'https://baike.baidu.com/item/%E6%97%A0%E7%BA%BF%E5%BA%94%E7%94%A8%E5%8D%8F%E8%AE%AE', '1', null);
