INSERT INTO `t_vocabulary` VALUES ('412', 'XSL', 'eXtensible Stylesheet Language', '可扩展样式表语言', 'XSL的英文是eXtensible Stylesheet Language，翻译成中文就是可扩展样式语言。它是W3C制定的另一种表现XML文档的样式语言。XSL是XML的应用，符合XML的语法规范，可以被XML的分析器处理。\n[1]', 'https://baike.baidu.com/item/%E5%8F%AF%E6%89%A9%E5%B1%95%E6%A0%B7%E5%BC%8F%E8%A1%A8%E8%AF%AD%E8%A8%80', '1', null);
